mapper.select:



        and t.client_id = #{clientId}

    and
        t.goods_shelf_code like concat('%', #{goodsShelfCode}, '%')


        and t.storehouse_code = #{storehouseCode}

    and
        t.goods_shelf_name like concat('%', #{goodsShelfName}, '%')


        and t.location_code = #{locationCode}


        and t.address = #{address}


        and t.status = #{status}


        and t.handle_status = #{handleStatus}


        and t.creator_account = #{creatorAccount}


        and t2.nick_name like concat('%', #{creatorAccountName}, '%')


        and date_format(t.create_date,'%y%m%d') >= date_format(#{beginTime},'%y%m%d')


        and date_format(t.create_date,'%y%m%d') <= date_format(#{endTime},'%y%m%d')


        and t.handle_status in
        (#{handleStatus})


        and t.storehouse_sid in
        (#{storehouseSid})


        and t.storehouse_location_sid in
        (
        #{storehouseLocationSid})


order by t.goods_shelf_code asc

mapper.update:
update s_bas_goods_shelf
client_id = #{clientId}, goods_shelf_code = #{goodsShelfCode}, storehouse_sid = #{storehouseSid}, storehouse_code = #{storehouseCode}, goods_shelf_name = #{goodsShelfName}, storehouse_location_sid = #{storehouseLocationSid}, location_code = #{locationCode}, address = #{address}, remark = #{remark}, status = #{status}, handle_status = #{handleStatus}, updater_account = #{updaterAccount}, update_date = #{updateDate}, confirmer_account = #{confirmerAccount}, confirm_date = #{confirmDate},
where goods_shelf_sid = #{goodsShelfSid}

