mapper.select:



        and t.client_id = #{clientId}

    and
        t.image_name like concat('%', #{imageName}, '%')


        and t.image_type = #{imageType}


        and t.image_description = #{imageDescription}


        and t.picture_path = #{picturePath}


        and t.status = #{status}


        and t.creator_account = #{creatorAccount}


        and date_format(t.create_date,'%y%m%d') >= date_format(#{beginTime},'%y%m%d')


        and date_format(t.create_date,'%y%m%d') <= date_format(#{endTime},'%y%m%d')


        and t.updater_account = #{updaterAccount}


        and t.update_date = #{updateDate}


        and t.confirmer_account = #{confirmerAccount}


        and t.confirm_date = #{confirmDate}


        and t.data_source_sys = #{dataSourceSys}


        ${params.dataScope}



        and t.image_code like concat('%', #{imageCode}, '%')



        and t2.nick_name like concat('%', #{creatorAccountName}, '%')



        and t.handle_status = #{handleStatus}


        and t.handle_status in
        (#{handleStatus})



order by t.image_code desc

mapper.update:
update s_bas_image
client_id = #{clientId}, image_code = #{imageCode}, image_name = #{imageName}, image_type = #{imageType}, image_description = #{imageDescription}, picture_path = #{picturePath}, remark = #{remark}, status = #{status}, handle_status = #{handleStatus}, updater_account = #{updaterAccount}, update_date = #{updateDate}, confirmer_account = #{confirmerAccount}, confirm_date = #{confirmDate},
where image_sid = #{imageSid}

