mapper.select:



        and (t.client_id
        like concat('%',
        #{item}, '%')
    )


        and t.laboratory_sid =#{laboratorySid}


        and t.laboratory_code like concat('%',#{laboratoryCode}, '%')


        and t.laboratory_name like concat('%', #{laboratoryName}, '%')


        and (t.vendor_sid = #{item}
    )


        and t.vendor_sid in (#{item})


        and t.country_region =#{countryRegion}


        and (t.address
        like concat('%',
        #{item}, '%')
    )


        and (t.status
        like concat('%',
        #{item}, '%')
    )


        and (t.handle_status
        like concat('%',
        #{item}, '%')
    )


        and t.handle_status in (#{item})


        and (t.creator_account
        like concat('%',
        #{item}, '%')
    )


        and (t2.nick_name
        like concat('%',
        #{item}, '%')
    )


        and date_format(t.create_date,'%y%m%d') >= date_format(#{beginTime},'%y%m%d')


        and date_format(t.create_date,'%y%m%d') <= date_format(#{endTime},'%y%m%d')


        and (t.updater_account
        like concat('%',
        #{item}, '%')
    )


        and t.update_date = #{updateDate}


        and (t.confirmer_account
        like concat('%',
        #{item}, '%')
    )


        and t.confirm_date = #{confirmDate}


        and (t.data_source_sys
        like concat('%',
        #{item}, '%')
    )


        ${params.dataScope}


order by t.laboratory_code desc

mapper.update:
update s_bas_laboratory
client_id = #{clientId}, laboratory_sid = #{laboratorySid}, laboratory_code = #{laboratoryCode}, laboratory_name = #{laboratoryName}, vendor_sid = #{vendorSid}, vendor_code = #{vendorCode}, country_region = #{countryRegion}, address = #{address}, remark = #{remark}, disable_remark = #{disableRemark}, status = #{status}, handle_status = #{handleStatus}, creator_account = #{creatorAccount}, create_date = #{createDate}, updater_account = #{updaterAccount}, update_date = #{updateDate}, confirmer_account = #{confirmerAccount}, confirm_date = #{confirmDate}, data_source_sys = #{dataSourceSys},
where laboratory_sid = #{laboratorySid}

update.foreach:
update s_bas_laboratory
client_id = #{clientId}, laboratory_sid = #{laboratorySid}, laboratory_code = #{laboratoryCode}, laboratory_name = #{laboratoryName}, vendor_sid = #{vendorSid}, vendor_code = #{vendorCode}, country_region = #{countryRegion}, address = #{address}, remark = #{remark}, disable_remark = #{disableRemark}, status = #{status}, handle_status = #{handleStatus}, creator_account = #{creatorAccount}, create_date = #{createDate}, updater_account = #{updaterAccount}, update_date = #{updateDate}, confirmer_account = #{confirmerAccount}, confirm_date = #{confirmDate}, data_source_sys = #{dataSourceSys},
where laboratory_sid = #{laboratorySid}

mapper.select:
select t.client_id, t.laboratory_sid, t.laboratory_code, t.laboratory_name, t.vendor_sid, t.vendor_code, t.country_region, t.address, t.remark, t.status, t.handle_status, t.creator_account, t.create_date, t.data_source_sys, t1.vendor_name,t1.short_name as vendor_short_name from s_bas_laboratory t LEFT JOIN s_bas_vendor t1 ON t.vendor_sid = t1.vendor_sid
and t.client_id = #{clientId}
and t.laboratory_sid =#{laboratorySid}
and t.laboratory_code like concat('%',#{laboratoryCode}, '%')
and t.laboratory_name like concat('%', #{laboratoryName}, '%')
and (t.vendor_sid = #{item} )
and t.vendor_sid in (#{item})
and t.country_region =#{countryRegion}
and t.status = #{status}
and t.handle_status = #{handleStatus}
and t.handle_status in (#{item})
and t.data_source_sys = #{dataSourceSys}
${params.dataScope}
order by CONVERT (t.laboratory_name USING gbk) asc
