mapper.update:
update s_bas_material_barcode_operate_level
client_id = #{clientId}, material_barcode_sid = #{materialBarcodeSid}, material_barcode = #{materialBarcode}, sale_station_sid = #{saleStationSid}, sale_station_code = #{saleStationCode}, operate_status = #{operateStatus}, operate_level = #{operateLevel}, product_level = #{productLevel}, erp_material_msku_code = #{erpMaterialMskuCode}, first_puchase_flag = #{firstPuchaseFlag}, second_puchase_flag = #{secondPuchaseFlag}, arrival_notice_flag_first_purchase = #{arrivalNoticeFlagFirstPurchase}, remark = #{remark}, updater_account = #{updaterAccount}, update_date = #{updateDate},
where material_barcode_operate_level_sid = #{materialBarcodeOperateLevelSid}

update.foreach:
update s_bas_material_barcode_operate_level
client_id = #{o.clientId}, material_barcode_sid = #{o.materialBarcodeSid}, material_barcode = #{o.materialBarcode}, sale_station_sid = #{o.saleStationSid}, sale_station_code = #{o.saleStationCode}, operate_status = #{o.operateStatus}, operate_level = #{o.operateLevel}, product_level = #{o.productLevel}, erp_material_msku_code = #{o.erpMaterialMskuCode}, first_puchase_flag = #{o.firstPuchaseFlag}, second_puchase_flag = #{o.secondPuchaseFlag}, arrival_notice_flag_first_purchase = #{o.arrivalNoticeFlagFirstPurchase}, remark = #{o.remark}, updater_account = #{o.updaterAccount}, update_date = #{o.updateDate},
where material_barcode_operate_level_sid = #{o.materialBarcodeOperateLevelSid}

mapper.select:
SELECT t.sale_station_sid, t.small_class_sid, t.middle_class_sid, t.big_class_sid, t.group_type, SUM(IF(t.operate_level ='HUOX',t.spu,0)) as spu_huo_x, SUM(IF(t.operate_level ='YUNX',t.spu,0)) as spu_yun_x, SUM(IF(t.operate_level ='XINX',t.spu,0)) as spu_xin_x, SUM(IF(t.operate_level ='LIUX',t.spu,0)) as spu_liu_x, SUM(IF(t.operate_level ='LIANGX',t.spu,0)) as spu_liang_x, SUM(IF(t.operate_level ='HENGX',t.spu,0)) as spu_heng_x, SUM(IF(t.operate_level ='ANX',t.spu,0)) as spu_an_x, t2.name as sale_station_name, t2.platform_dianshang, t2.region, t3.node_name as small_class_name, t4.node_name as middle_class_name, t5.node_name as big_class_name FROM ( SELECT t.*, t1.operate_level, t1.spu FROM ( SELECT t.sale_station_sid, t.small_class_sid, t.middle_class_sid, t.big_class_sid, t.group_type FROM ( SELECT t.material_barcode_sid, t.sale_station_sid, t.operate_level, t1.small_class_sid, t1.middle_class_sid, t1.big_class_sid, t1.group_type FROM s_bas_material_barcode_operate_level t LEFT JOIN s_bas_material_barcode t0 ON t.material_barcode_sid = t0.barcode_sid LEFT JOIN s_bas_material t1 ON t0.material_sid = t1.material_sid
and t.client_id = #{clientId}
and t.material_barcode like concat('%', #{materialBarcode}, '%')
) t GROUP BY t.sale_station_sid, t1.small_class_sid, t1.middle_class_sid, t1.big_class_sid, t1.group_type ) t LEFT JOIN ( SELECT COUNT(t.material_barcode_sid) as spu, t.sale_station_sid, t.operate_level, t.small_class_sid, t.middle_class_sid, t.big_class_sid, t.group_type FROM ( SELECT t.material_barcode_sid, t.sale_station_sid, t.operate_level, t1.small_class_sid, t1.middle_class_sid, t1.big_class_sid, t1.group_type FROM s_bas_material_barcode_operate_level t LEFT JOIN s_bas_material_barcode t0 ON t.material_barcode_sid = t0.barcode_sid LEFT JOIN s_bas_material t1 ON t0.material_sid = t1.material_sid
and t.client_id = #{clientId}
and t.material_barcode like concat('%', #{materialBarcode}, '%')
) t GROUP BY t.sale_station_sid, t.operate_level, t1.small_class_sid, t1.middle_class_sid, t1.big_class_sid, t1.group_type ) t1 ON t.sale_station_sid = t1.sale_station_sid and t.group_type <=> t1.group_type and t.small_class_sid <=> t1.small_class_sid and t.middle_class_sid <=> t1.middle_class_sid and t.big_class_sid <=> t1.big_class_sid ) t LEFT JOIN s_con_sale_station t2 ON t.sale_station_sid = t2.sid LEFT JOIN s_con_material_class t3 ON t.small_class_sid = t3.material_class_sid LEFT JOIN s_con_material_class t4 ON t.middle_class_sid = t4.material_class_sid LEFT JOIN s_con_material_class t5 ON t.big_class_sid = t5.material_class_sid
and t.small_class_sid = #{smallClassSid}
and t.middle_class_sid = #{middleClassSid}
and t.big_class_sid = #{bigClassSid}
and t.group_type = #{groupType}
and t.group_type in (#{groupType})
and t.sale_station_sid in (#{saleStationSid})
and t2.platform_dianshang in (#{platformDianshang})
and t2.region in (#{region})
GROUP BY t.sale_station_sid, t.small_class_sid, t.middle_class_sid, t.big_class_sid, t.group_type LIMIT ${pageBegin},${pageSize}
