mapper.select:



        and t.client_id = #{clientId}


        and t.field_name like concat('%', #{fieldName}, '%')


        and t.field_type = #{fieldType}


        and t.field_type in
        (#{item})


        and t.field_text_length = #{fieldTextLength}


        and t.field_decimal_length = #{fieldDecimalLength}


        and t.is_null_flag = #{isNullFlag}


        and t.is_print = #{isPrint}


        and t.field_desc like concat('%', #{fieldDesc}, '%')


        and t.company_sid = #{companySid}


        and t.status = #{status}


        and t.handle_status = #{handleStatus}


        and t.handle_status in
        (#{item})


        and t.creator_account = #{creatorAccount}


        and t1.nick_name like concat('%', #{creatorAccountName}, '%')


        and date_format(t.create_date,'%y%m%d') >= date_format(#{beginTime},'%y%m%d')


        and date_format(t.create_date,'%y%m%d') <= date_format(#{endTime},'%y%m%d')


        and t.updater_account = #{updaterAccount}


        and t.update_date = #{updateDate}


        and t.data_source_sys = #{dataSourceSys}


ORDER BY t.field_name ASC, t.create_date desc

mapper.update:
update s_bas_material_certificate_field
field_name = #{fieldName}, field_type = #{fieldType}, field_text_length = #{fieldTextLength}, field_decimal_length = #{fieldDecimalLength}, is_null_flag = #{isNullFlag}, is_print = #{isPrint}, field_desc = #{fieldDesc}, company_sid = #{companySid}, remark = #{remark}, status = #{status}, handle_status = #{handleStatus}, updater_account = #{updaterAccount}, update_date = #{updateDate}, confirmer_account = #{confirmerAccount}, confirm_date = #{confirmDate}, data_source_sys = #{dataSourceSys},
where material_certificate_field_sid = #{materialCertificateFieldSid}

update.foreach:
update s_bas_material_certificate_field
field_name = #{o.fieldName}, field_type = #{o.fieldType}, field_text_length = #{o.fieldTextLength}, field_decimal_length = #{o.fieldDecimalLength}, is_null_flag = #{o.isNullFlag}, is_print = #{o.isPrint}, field_desc = #{o.fieldDesc}, company_sid = #{o.companySid}, remark = #{o.remark}, status = #{o.status}, handle_status = #{o.handleStatus}, updater_account = #{o.updaterAccount}, update_date = #{o.updateDate}, confirmer_account = #{o.confirmerAccount}, confirm_date = #{o.confirmDate}, data_source_sys = #{o.dataSourceSys},
where material_certificate_field_sid = #{o.materialCertificateFieldSid}
