mapper.select:
select t.material_package_sid, t.package_code, t.company_sid, t.package_name, t.product_season_sid, t.package_category, t.customer_sid, t.company_brand_sid, t.customer_brand_sid, t.remark, t.disable_remark, t.status, t.handle_status, t.creator_account, t.create_date, t.updater_account, t.update_date, t.confirmer_account, t.confirm_date, t1.product_season_name,t2.company_name, t3.brand_name as company_brand_name , t4.customer_name, ifnull(t4.short_name,t4.customer_name) as customer_short_name, t5.brand_name as customer_brand_name, t6.nick_name AS creator_account_name, t7.nick_name AS updater_account_name, t8.nick_name AS confirmer_account_name from s_bas_material_package t left join s_bas_product_season t1 on t1.product_season_sid=t.product_season_sid left join s_bas_company t2 on t2.company_sid =t.company_sid left join s_bas_company_brand t3 on t3.company_brand_sid =t.company_brand_sid left join s_bas_customer t4 on t4.customer_sid=t.customer_sid left join s_bas_customer_brand t5 on t5.customer_brand_sid=t.customer_brand_sid LEFT JOIN sys_user t6 ON t.creator_account = t6.user_name LEFT JOIN sys_user t7 ON t.updater_account = t7.user_name LEFT JOIN sys_user t8 ON t.confirmer_account = t8.user_name
and t.package_code like concat('%', #{packageCode}, '%')
and ( t.package_name like concat('%', #{packageName}, '%'))
and t.company_sid = #{companySid}
and t.company_brand_sid = #{companyBrandSid}
and t.customer_sid = #{customerSid}
and t.customer_brand_sid = #{customerBrandSid}
and t.customer_sid in (#{item})
and t.product_season_sid = #{productSeasonSid}
and t.product_season_sid in (#{item})
and t.status = #{status}
and t.handle_status in (#{handleStatus})
and t.handle_status = #{handleStatus}
and ( t6.nick_name like concat('%', #{item}, '%'))
and date_format(t.create_date,'%y%m%d') >= date_format(#{createDateStart},'%y%m%d')
and date_format(t.create_date,'%y%m%d') <= date_format(#{createDateEnd},'%y%m%d')
and date_format(t.create_date,'%y%m%d') >= date_format(#{beginTime},'%y%m%d')
and date_format(t.create_date,'%y%m%d') <= date_format(#{endTime},'%y%m%d')
order by t.create_date desc

mapper.update:
update s_bas_material_package
material_package_sid = #{materialPackageSid}, package_code = #{packageCode}, package_name = #{packageName}, product_season_sid = #{productSeasonSid}, company_brand_sid = #{companyBrandSid}, customer_sid = #{customerSid}, customer_brand_sid = #{customerBrandSid}, company_sid=#{companySid}, remark = #{remark}, disable_remark = #{disableRemark}, status = #{status}, handle_status = #{handleStatus}, creator_account = #{creatorAccount}, create_date = #{createDate}, updater_account = #{updaterAccount}, update_date = #{updateDate}, confirmer_account = #{confirmerAccount}, confirm_date = #{confirmDate}, data_source_sys = #{dataSourceSys},
where material_package_sid = #{materialPackageSid}

update.foreach:
update s_bas_material_package
material_package_sid = #{materialPackageSid}, package_code = #{packageCode}, package_name = #{packageName}, product_season_sid = #{productSeasonSid}, package_category=#{packageCategory} customer_sid = #{customerSid}, customer_brand = #{customerBrand}, remark = #{remark}, disable_remark = #{disableRemark}, status = #{status}, handle_status = #{handleStatus}, creator_account = #{creatorAccount}, create_date = #{createDate}, updater_account = #{updaterAccount}, update_date = #{updateDate}, confirmer_account = #{confirmerAccount}, confirm_date = #{confirmDate}, data_source_sys = #{dataSourceSys},
where material_package_sid = #{materialPackageSid}
