mapper.update:
update s_bas_material_sale_station
client_id = #{clientId}, material_sid = #{materialSid}, sale_station_sid = #{saleStationSid}, sale_station_code = #{saleStationCode}, operate_status = #{operateStatus}, operate_level = #{operateLevel}, remark = #{remark}, updater_account = #{updaterAccount}, update_date = #{updateDate},
where material_sale_station_sid = #{materialSaleStationSid}

update.foreach:
update s_bas_material_sale_station
operate_status = #{o.operateStatus}, operate_level = #{o.operateLevel}, remark = #{o.remark}, updater_account = #{o.updaterAccount}, update_date = #{o.updateDate},
where material_sale_station_sid = #{o.materialSaleStationSid}

mapper.select:
SELECT t.spu_count, t.sale_station_sid, t.small_class_sid, t.middle_class_sid, t.big_class_sid, t.group_type, SUM(IF(t.operate_status ='CL',t.spu,0)) as spu_stock, SUM(IF(t.operate_status ='JHZL',t.spu,0)) as spu_plan_inc, SUM(IF(t.operate_status ='GH',t.spu,0)) as spu_plan, SUM(IF(t.operate_status ='QZ',t.spu,0)) as spu_discard, t2.name as sale_station_name, t2.region, t2.platform_dianshang, t3.node_name as small_class_name, t4.node_name as middle_class_name, t5.node_name as big_class_name FROM ( SELECT t.spu_count, t.sale_station_sid, t.small_class_sid, t.middle_class_sid, t.big_class_sid, t.group_type, t1.operate_status, t1.spu FROM ( SELECT COUNT(DISTINCT(t.material_sid)) as spu_count, t.sale_station_sid, t1.small_class_sid, t1.middle_class_sid, t1.big_class_sid, t1.group_type FROM s_bas_material_sale_station t LEFT JOIN s_bas_material t1 ON t.material_sid = t1.material_sid
and t.client_id = #{clientId}
GROUP BY t.sale_station_sid, t1.small_class_sid, t1.middle_class_sid, t1.big_class_sid, t1.group_type ) t LEFT JOIN ( SELECT COUNT(DISTINCT(t.material_sid)) as spu, t.operate_status, t.sale_station_sid, t1.small_class_sid, t1.middle_class_sid, t1.big_class_sid, t1.group_type FROM s_bas_material_sale_station t LEFT JOIN s_bas_material t1 ON t.material_sid = t1.material_sid
and t.client_id = #{clientId}
GROUP BY t.sale_station_sid, t.operate_status, t1.small_class_sid, t1.middle_class_sid, t1.big_class_sid, t1.group_type ) t1 ON t.sale_station_sid = t1.sale_station_sid and t.group_type <=> t1.group_type and t.small_class_sid <=> t1.small_class_sid and t.middle_class_sid <=> t1.middle_class_sid and t.big_class_sid <=> t1.big_class_sid ) t LEFT JOIN s_con_sale_station t2 ON t.sale_station_sid = t2.sid LEFT JOIN s_con_material_class t3 ON t.small_class_sid = t3.material_class_sid LEFT JOIN s_con_material_class t4 ON t.middle_class_sid = t4.material_class_sid LEFT JOIN s_con_material_class t5 ON t.big_class_sid = t5.material_class_sid
and t.small_class_sid = #{smallClassSid}
and t.middle_class_sid = #{middleClassSid}
and t.big_class_sid = #{bigClassSid}
and t.group_type = #{groupType}
and t.group_type in (#{groupType})
and t.sale_station_sid in (#{saleStationSid})
and t2.platform_dianshang in (#{platformDianshang})
and t2.region in (#{region})
GROUP BY t.sale_station_sid, t.small_class_sid, t.middle_class_sid, t.big_class_sid, t.group_type LIMIT ${pageBegin},${pageSize}
