mapper.select:
select t.product_season_sid, t.product_season_code, t.product_season_name, t.year, t.season_code, t.status, t.handle_status
from s_bas_product_season t


        and t.handle_status = #{handleStatus}


        and t.status = #{status}


order by t.product_season_name desc




        and t.product_season_sid = #{productSeasonSid}


        and t.product_season_name like concat('%', #{productSeasonName}, '%')


        and t.year in
        (#{year})


        and t.season_code in
        (#{seasonCode})


        and t.product_season_code like concat('%', #{productSeasonCode}, '%')


        and t.product_season_stage in
        (
        #{productSeasonStage})


        and t.product_season_stage = #{productSeasonStage}


        and t.status = #{status}


        and t.handle_status in
        (#{handleStatus})


        and t.handle_status = #{handleStatus}


        and n1.nick_name like concat('%', #{creatorAccount}, '%')


        and date_format(t.create_date,'%y%m%d') >= date_format(#{beginTime},'%y%m%d')


        and date_format(t.create_date,'%y%m%d') <= date_format(#{endTime},'%y%m%d')


        and (
            t.updater_account like concat('%', #{item}, '%')
    )


        and t.update_date = #{updateDate}


        and (
            t.confirmer_account like concat('%', #{item}, '%')
    )


        and t.confirm_date = #{confirmDate}


        and t.data_source_sys = #{dataSourceSys}


        ${params.dataScope}


order by t.product_season_code desc, t.create_date desc

mapper.update:
update s_bas_product_season
product_season_code = #{productSeasonCode}, product_season_name = #{productSeasonName}, year = #{year}, season_code = #{seasonCode}, product_season_stage = #{productSeasonStage}, remark = #{remark}, disable_remark = #{disableRemark}, status = #{status}, handle_status = #{handleStatus}, updater_account = #{updaterAccount}, update_date = #{updateDate}, confirmer_account = #{confirmerAccount}, confirm_date = #{confirmDate}, data_source_sys = #{dataSourceSys},
where product_season_sid = #{productSeasonSid}

update.foreach:
update s_bas_product_season
product_season_code = #{productSeasonCode}, product_season_name = #{productSeasonName}, year = #{year}, season_code = #{seasonCode}, product_season_stage = #{productSeasonStage}, remark = #{remark}, disable_remark = #{disableRemark}, status = #{status}, handle_status = #{handleStatus}, updater_account = #{updaterAccount}, update_date = #{updateDate}, confirmer_account = #{confirmerAccount}, confirm_date = #{confirmDate}, data_source_sys = #{dataSourceSys},
where product_season_sid = #{productSeasonSid}
