mapper.select:



        and t.client_id = #{clientId}


        and t.season_vendor_sid = #{seasonVendorSid}


        and t.season_vendor_sid in
        (#{item})


        and t.product_season_sid = #{productSeasonSid}


        and t.product_season_sid in
        (#{item})


        and t.product_season_code = #{productSeasonCode}


        and t.vendor_sid = #{vendorSid}


        and t.vendor_sid in
        (#{item})


        and t.vendor_code = #{vendorCode}


        and t.company_sid = #{companySid}


        and t.company_sid in
        (#{item})


        and t.company_code = #{companyCode}


        and t.company_brand_sid = #{companyBrandSid}


        and t.company_brand_sid in
        (#{item})


        and t.company_brand_code = #{companyBrandCode}


        and t.is_kuaifan_vendor = #{isKuaifanVendor}


        and t.status = #{status}


        and t.handle_status = #{handleStatus}


        and t.handle_status in
        (#{item})


        and t.creator_account = #{creatorAccount}


        and t.creator_account in
        (#{item})


        and t2.nick_name like concat('%', #{creatorAccountName}, '%')


        and date_format(t.create_date,'%y%m%d') >= date_format(#{beginTime},'%y%m%d')


        and date_format(t.create_date,'%y%m%d') <= date_format(#{endTime},'%y%m%d')


        and t.updater_account = #{updaterAccount}


        and t.update_date = #{updateDate}


        and t.confirmer_account = #{confirmerAccount}


        and t.confirm_date = #{confirmDate}


        and t.data_source_sys = #{dataSourceSys}


        ${params.dataScope}


order by t.season_vendor_code desc

mapper.update:
update s_bas_season_vendor
product_season_sid = #{productSeasonSid}, product_season_code = #{productSeasonCode}, vendor_sid = #{vendorSid}, vendor_code = #{vendorCode}, company_sid = #{companySid}, company_code = #{companyCode}, company_brand_sid = #{companyBrandSid}, company_brand_code = #{companyBrandCode}, is_kuaifan_vendor = #{isKuaifanVendor}, remark = #{remark}, status = #{status}, handle_status = #{handleStatus}, updater_account = #{updaterAccount}, update_date = #{updateDate}, confirmer_account = #{confirmerAccount}, confirm_date = #{confirmDate},
where season_vendor_sid = #{seasonVendorSid}

update.foreach:
update s_bas_season_vendor
product_season_sid = #{o.productSeasonSid}, product_season_code = #{o.productSeasonCode}, vendor_sid = #{o.vendorSid}, vendor_code = #{o.vendorCode}, company_sid = #{o.companySid}, company_code = #{o.companyCode}, company_brand_sid = #{o.companyBrandSid}, company_brand_code = #{o.companyBrandCode}, is_kuaifan_vendor = #{o.isKuaifanVendor}, remark = #{o.remark}, status = #{o.status}, handle_status = #{o.handleStatus}, updater_account = #{o.updaterAccount}, update_date = #{o.updateDate}, confirmer_account = #{o.confirmerAccount}, confirm_date = #{o.confirmDate},
where season_vendor_sid = #{o.seasonVendorSid}
