mapper.select:
select  t.sku_group_sid, t.sku_group_code, t.sku_group_name, t.sku_type, t.status, t.handle_status
from s_bas_sku_group t


        and t.sku_type = #{skuType}


        and t.status = #{status}


        and t.handle_status = #{handleStatus}


order by t.sku_group_name asc




        and t.up_down_suit in
        (#{upDownSuit})


        and t.handle_status in
        (#{handleStatus})


        and t.sku_type in
        (#{skuType})


        and t.customer_sid in
        (#{customerSid})


        and t.sku_group_sid = #{skuGroupSid}


        and (  t.sku_group_code like concat('%', #{item}, '%'))


        and (  t.sku_group_name like concat('%', #{item}, '%'))


        and ( t.sku_type like concat('%',
        #{item}, '%')
    )


        and ( t.up_down_suit like concat('%',
        #{item}, '%')
    )


        and t.customer_sid =#{customerSid}


        and ( t.status like concat('%',
        #{item}, '%')
    )


        and ( t.handle_status like concat('%',
        #{item}, '%')
    )


        and ( t3.nick_name like concat('%',
        #{item}, '%')
    )


        and date_format(t.create_date,'%y%m%d') >= date_format(#{beginTime},'%y%m%d')


        and date_format(t.create_date,'%y%m%d') <= date_format(#{endTime},'%y%m%d')


        and ( t.updater_account like concat('%',
        #{item}, '%')
    )


        and t.update_date = #{updateDate}


        and ( t.confirmer_account like
        concat('%',
        #{item}, '%')
    )


        and t.confirm_date = #{confirmDate}


        and ( t.data_source_sys like concat('%',
        #{item}, '%')
    )


        ${params.dataScope}


order by t.sku_group_code desc, t.create_date desc

mapper.update:
update s_bas_sku_group
sku_group_code = #{skuGroupCode}, sku_group_name = #{skuGroupName}, sku_type = #{skuType}, up_down_suit = #{upDownSuit}, customer_sid = #{customerSid}, remark = #{remark}, disable_remark = #{disableRemark}, status = #{status}, handle_status = #{handleStatus}, updater_account = #{updaterAccount}, update_date = #{updateDate}, confirmer_account = #{confirmerAccount}, confirm_date = #{confirmDate}, data_source_sys = #{dataSourceSys},
where sku_group_sid = #{skuGroupSid}

update.foreach:
update s_bas_sku_group
sku_group_code = #{skuGroupCode}, sku_group_name = #{skuGroupName}, sku_type = #{skuType}, up_down_suit = #{upDownSuit}, customer_sid = #{customerSid}, remark = #{remark}, disable_remark = #{disableRemark}, status = #{status}, handle_status = #{handleStatus}, updater_account = #{updaterAccount}, update_date = #{updateDate}, confirmer_account = #{confirmerAccount}, confirm_date = #{confirmDate}, data_source_sys = #{dataSourceSys},
where sku_group_sid = #{skuGroupSid}
