mapper.select:



        and t4.storehouse_name like concat('%', #{storehouseName}, '%')


        and t4.storehouse_code like concat('%', #{storehouseCode}, '%')

    and t.storehouse_sid =
        #{storehouseSid}

    and t.storehouse_contact_sid =
        #{storehouseContactSid}

    and t.contacter_name like concat('%',
        #{contacterName}, '%')

    and t.contacter_position =
        #{contacterPosition}

    and t.contacter_mobphone =
        #{contacterMobphone}

    and t.contacter_telephone =
        #{contacterTelephone}

    and t.contacter_fax = #{contacterFax}
    and t.contacter_email = #{contacterEmail}
    and t.contacter_address =
        #{contacterAddress}

    and t.contacter_department =
        #{contacterDepartment}

    and t.business_type = #{businessType}
    and t.is_default = #{isDefault}
    and t.creator_account = #{creatorAccount}
    and t.create_date = #{createDate}
    and t.updater_account = #{updaterAccount}
    and t.update_date = #{updateDate}
    and t.data_source_sys = #{dataSourceSys}

order by storehouse_contact_sid desc
