ifnull(short_name,vendor_name) as short_name,
vendor_code,vendor_type,status,handle_status
from s_bas_vendor


        and client_id = #{clientId}


        and (vendor_code like concat('%', #{vendorCodeName}, '%')
        or vendor_name like concat('%', #{vendorCodeName}, '%')
        or short_name like concat('%', #{vendorCodeName}, '%'))


        and vendor_sid = #{vendorSid}


        and status = #{status}


        and handle_status = #{handleStatus}


order by vendor_name asc

