mapper.select:



        and (t.client_id
        like concat('%',
        #{item}, '%')
    )


        and t.vendor_tag_item_sid =#{vendorTagItemSid}


        and t.vendor_tag_sid =#{vendorTagSid}


        and t.vendor_sid =#{vendorSid}


        and t.sort =#{sort}


        and (t.creator_account
        like concat('%',
        #{item}, '%')
    )


        and date_format(t.create_date,'%y%m%d') >= date_format(#{beginTime},'%y%m%d')


        and date_format(t.create_date,'%y%m%d') <= date_format(#{endTime},'%y%m%d')


        and (t.updater_account
        like concat('%',
        #{item}, '%')
    )


        and t.update_date = #{updateDate}


        and (t.data_source_sys
        like concat('%',
        #{item}, '%')
    )


        ${params.dataScope}


order by t.create_date desc

mapper.update:
update s_bas_vendor_tag_item
client_id = #{clientId}, vendor_tag_item_sid = #{vendorTagItemSid}, vendor_tag_sid = #{vendorTagSid}, vendor_sid = #{vendorSid}, sort = #{sort}, remark = #{remark}, creator_account = #{creatorAccount}, create_date = #{createDate}, updater_account = #{updaterAccount}, update_date = #{updateDate}, data_source_sys = #{dataSourceSys},
where vendor_tag_item_sid = #{vendorTagItemSid}

update.foreach:
update s_bas_vendor_tag_item
client_id = #{clientId}, vendor_tag_item_sid = #{vendorTagItemSid}, vendor_tag_sid = #{vendorTagSid}, vendor_sid = #{vendorSid}, sort = #{sort}, remark = #{remark}, creator_account = #{creatorAccount}, create_date = #{createDate}, updater_account = #{updaterAccount}, update_date = #{updateDate}, data_source_sys = #{dataSourceSys},
where vendor_tag_item_sid = #{vendorTagItemSid}
