mapper.select:



        and (t.client_id like concat('%',
        #{item}, '%')
    )


        and t.material_class_sid in
        (#{item})


        and t.material_class_sid =#{materialClassSid}


        and (t.node_code like concat('%',
        #{item}, '%')
    )

    and
        t.node_name like concat('%', #{nodeName}, '%')


        and (t.node_type like concat('%',
        #{item}, '%')
    )


        and t.node_type = #{materialType}


        and t.level =#{level}

    and
        t.node_short_name like concat('%', #{nodeShortName}, '%')


        and t.parent_code_sid =#{parentCodeSid}


        and (t.material_category
        like concat('%',
        #{item}, '%')
    )


        and t.node_sort =#{nodeSort}


        and (t.status like concat('%',
        #{item}, '%')
    )


        and (t.handle_status like
        concat('%',
        #{item}, '%')
    )


        and (t.creator_account like
        concat('%',
        #{item}, '%')
    )


        and date_format(t.create_date,'%y%m%d') >= date_format(#{beginTime},'%y%m%d')


        and date_format(t.create_date,'%y%m%d') <= date_format(#{endTime},'%y%m%d')


        and (t.updater_account like
        concat('%',
        #{item}, '%')
    )


        and t.update_date = #{updateDate}


        and (t.confirmer_account
        like concat('%',
        #{item}, '%')
    )


        and t.confirm_date = #{confirmDate}


        and (t.data_source_sys like
        concat('%',
        #{item}, '%')
    )


        and (t.zipper_flag like
        concat('%',
        #{item}, '%')
    )


        ${params.dataScope}


order by t.level asc, t.node_code asc

mapper.update:
update s_con_material_class
material_class_sid = #{materialClassSid}, node_code = #{nodeCode}, node_name = #{nodeName}, node_type = #{nodeType}, level = #{level}, node_short_name = #{nodeShortName}, parent_code_sid = #{parentCodeSid}, material_category = #{materialCategory}, node_sort = #{nodeSort}, remark = #{remark}, status = #{status}, handle_status = #{handleStatus}, updater_account = #{updaterAccount}, update_date = #{updateDate}, confirmer_account = #{confirmerAccount}, confirm_date = #{confirmDate}, data_source_sys = #{dataSourceSys}, zipper_flag = #{zipperFlag},
where material_class_sid = #{materialClassSid}

update.foreach:
update s_con_material_class
client_id = #{clientId}, material_class_sid = #{materialClassSid}, node_code = #{nodeCode}, node_name = #{nodeName}, node_type = #{nodeType}, level = #{level}, node_short_name = #{nodeShortName}, parent_code_sid = #{parentCodeSid}, material_category = #{materialCategory}, node_sort = #{nodeSort}, remark = #{remark}, status = #{status}, handle_status = #{handleStatus}, creator_account = #{creatorAccount}, create_date = #{createDate}, updater_account = #{updaterAccount}, update_date = #{updateDate}, confirmer_account = #{confirmerAccount}, confirm_date = #{confirmDate}, data_source_sys = #{dataSourceSys}, zipper_flag = #{zipperFlag},
where material_class_sid = #{materialClassSid}

mapper.select:
SELECT T1.material_class_sid,T1.node_code,T1.node_name,T1.parent_code_sid,T1.level,T2.sort
FROM s_con_material_class T1
INNER JOIN (
    SELECT
        @id AS _id,
        (SELECT @id := parent_code_sid FROM s_con_material_class WHERE material_class_sid = _id) AS parent_code_sid,
        @sort := @sort + 1 AS sort
    FROM
        (SELECT @id := ${materialClassSid}, @sort := 0) T2_1, s_con_material_class T2_2
) T2 ON T1.material_class_sid = T2._id
ORDER BY T2.sort DESC;

select t.client_id, t.material_class_sid, t.node_code, t.node_code AS material_class_code, t.node_name, t.node_name AS material_class_name, t.node_type, t.node_type AS material_type, t.zipper_flag, t.level, t.node_short_name, t.parent_code_sid, t.material_category, t.node_sort, t.data_source_sys, t1.name AS node_type_name FROM s_con_material_class t LEFT JOIN s_con_material_type t1 ON t.node_type = t1.code
AND t.parent_code_sid = #{parentCodeSid}
AND t.level = #{level}
AND t.status = #{status}
AND t.handle_status = #{handleStatus}
