mapper.select:



        and t.client_id = #{clientId}


        and t.material_sid = #{materialSid}


        and t.material_code like concat('%', #{materialCode}, '%')


        and t.sku1_sid = #{sku1Sid}


        and t.sku1_code = #{sku1Code}


        and t.sku1_name like concat('%', #{sku1Name}, '%')


        and t.sku2_sid = #{sku2Sid}


        and t.sku2_code = #{sku2Code}


        and t.sku2_name like concat('%', #{sku2Name}, '%')


        and t.plant_sid = #{plantSid}


        and t.plant_sid in
        (#{plantSid})


        and t.plant_code = #{plantCode}


        and t.material_price = #{materialPrice}


        and t.price = #{price}


        and t.special_craft_price = #{specialCraftPrice}


        and t.other_price = #{otherPrice}


        and t.sale_price = #{salePrice}


        and t.currency = #{currency}


        and t.currency_unit = #{currencyUnit}


        and t.product_cost_date = #{productCostDate}


        and t.handle_status = #{handleStatus}


        and t.handle_status in
        (#{handleStatus})


        and t.creator_account = #{creatorAccount}


        and t2.nick_name like concat('%', #{creatorAccountName}, '%')


        and date_format(t.create_date,'%y%m%d') >= date_format(#{beginTime},'%y%m%d')


        and date_format(t.create_date,'%y%m%d') <= date_format(#{endTime},'%y%m%d')


        and t.updater_account = #{updaterAccount}


        and t.update_date = #{updateDate}


        and t.confirmer_account = #{confirmerAccount}


        and t.confirm_date = #{confirmDate}


        and t.data_source_sys = #{dataSourceSys}


        ${params.dataScope}


ORDER BY t.record_cost_code DESC

mapper.update:
update s_cos_record_product_actual_cost
material_sid = #{materialSid}, material_code = #{materialCode}, sku1_sid = #{sku1Sid}, sku1_code = #{sku1Code}, sku1_name = #{sku1Name}, sku2_sid = #{sku2Sid}, sku2_code = #{sku2Code}, sku2_name = #{sku2Name}, plant_sid = #{plantSid}, plant_code = #{plantCode}, material_price = #{materialPrice}, price = #{price}, special_craft_price = #{specialCraftPrice}, other_price = #{otherPrice}, sale_price = #{salePrice}, currency = #{currency}, currency_unit = #{currencyUnit}, product_cost_date = #{productCostDate}, remark = #{remark}, handle_status = #{handleStatus}, updater_account = #{updaterAccount}, update_date = #{updateDate}, confirmer_account = #{confirmerAccount}, confirm_date = #{confirmDate},
where record_cost_sid = #{recordCostSid}

update.foreach:
update s_cos_record_product_actual_cost
material_sid = #{o.materialSid}, material_code = #{o.materialCode}, sku1_sid = #{o.sku1Sid}, sku1_code = #{o.sku1Code}, sku1_name = #{o.sku1Name}, sku2_sid = #{o.sku2Sid}, sku2_code = #{o.sku2Code}, sku2_name = #{o.sku2Name}, plant_sid = #{o.plantSid}, plant_code = #{o.plantCode}, material_price = #{o.materialPrice}, price = #{o.price}, special_craft_price = #{o.specialCraftPrice}, other_price = #{o.otherPrice}, sale_price = #{o.salePrice}, currency = #{o.currency}, currency_unit = #{o.currencyUnit}, product_cost_date = #{o.productCostDate}, remark = #{o.remark}, handle_status = #{o.handleStatus}, updater_account = #{o.updaterAccount}, update_date = #{o.updateDate}, confirmer_account = #{o.confirmerAccount}, confirm_date = #{o.confirmDate},
where record_cost_sid = #{o.recordCostSid}
