mapper.select:



        and t.client_id = #{clientId}


        and t.delivery_note_sid =#{deliveryNoteSid}


        and t.delivery_note_sid in
        (#{item})


        and t.delivery_note_code like concat('%',#{deliveryNoteCode}, '%')


        and t.vendor_sid =#{vendorSid}


        and t.vendor_sid in
        (#{item})


        and t.document_date = #{documentDate}


        and t.plant_sid =#{plantSid}


        and t.plant_sid in
        (#{item})


        and t.document_type = #{documentType}


        and t.document_type in
        (#{item})


        and t.business_type = #{businessType}


        and t.business_type in
        (#{item})


        and (t.consignee like concat('%',
        #{item}, '%')
    )


        and (t.consignee_phone like
        concat('%',
        #{item}, '%')
    )


        and (t.consignee_addr like
        concat('%',
        #{item}, '%')
    )


        and t.carrier = #{carrier}


        and t.carrier in
        (#{item})


        and (t.carrier_note_code
        like concat('%',
        #{item}, '%')
    )


        and (
        t.supplier_delivery_code like concat('%',
        #{item}, '%')
    )


        and t.expected_arrival_date = #{expectedArrivalDate}


        and (t.in_stock_status like
        concat('%',
        #{item}, '%')
    )


        and (t.operator like concat('%',
        #{item}, '%')
    )


        and t.company_sid =#{companySid}


        and t.material_category = #{materialCategory}


        and t.handle_status = #{handleStatus}


        and t.handle_status in
        (#{item})


        and (t.creator_account like
        concat('%',
        #{item}, '%')
    )


        and t.creator_account in
        (#{item})


        and date_format(t.create_date,'%y%m%d') >= date_format(#{beginTime},'%y%m%d')


        and date_format(t.create_date,'%y%m%d') <= date_format(#{endTime},'%y%m%d')


        and (t.updater_account like
        concat('%',
        #{item}, '%')
    )


        and t.update_date = #{updateDate}


        and (t.confirmer_account
        like concat('%',
        #{item}, '%')
    )


        and t.confirm_date = #{confirmDate}


        and (t.data_source_sys like
        concat('%', #{item}, '%')
    )


        and t12.manufacture_order_code like concat('%',#{manufactureOrderCode}, '%')


        ${params.dataScope}


group by t.delivery_note_sid
order by t.delivery_note_code desc, t.create_date desc

