mapper.select:



        and t.client_id = #{clientId}


        and t.category_plan_sid = #{categoryPlanSid}


        and t.category_plan_sid in
        (#{categoryPlanSid})


        and t.category_plan_code like concat('%', #{categoryPlanCode}, '%')


        and t.year = #{year}


        and t.year in
        (#{year})


        and t.company_sid = #{companySid}


        and t.company_sid in
        (#{companySid})


        and t.company_code = #{companyCode}


        and t.brand_code = #{brandCode}


        and t.brand_code in
        (#{brandCode})


        and t.product_season_sid = #{productSeasonSid}


        and t.product_season_sid in
        (#{item})


        and EXISTS (SELECT a.category_plan_item_sid FROM s_dev_category_plan_item a
            WHERE a.category_plan_sid = t.category_plan_sid AND a.group_type = #{groupType})


        and EXISTS (SELECT a.category_plan_item_sid FROM s_dev_category_plan_item a
                    WHERE a.category_plan_sid = t.category_plan_sid AND a.group_type  in
            (#{groupType})


        and t.cancel_remark = #{cancelRemark}


        and t.handle_status = #{handleStatus}


        and t.handle_status in
        (#{handleStatus})


        and t.creator_account = #{creatorAccount}


        and t2.nick_name like concat('%', #{creatorAccountName}, '%')


        and date_format(t.create_date,'%y%m%d') = date_format(#{createDate},'%y%m%d')


        and date_format(t.create_date,'%y%m%d') >= date_format(#{beginTime},'%y%m%d')


        and date_format(t.create_date,'%y%m%d') <= date_format(#{endTime},'%y%m%d')


        and t.updater_account = #{updaterAccount}


        and t.update_date = #{updateDate}


        and t.confirmer_account = #{confirmerAccount}


        and t.confirm_date = #{confirmDate}


        and t.data_source_sys = #{dataSourceSys}


        ${params.dataScope}


order by t.create_date desc

mapper.update:
update s_dev_category_plan
client_id = #{clientId}, category_plan_code = #{categoryPlanCode}, year = #{year}, company_sid = #{companySid}, company_code = #{companyCode}, brand_code = #{brandCode}, product_season_sid = #{productSeasonSid}, cancel_remark = #{cancelRemark}, remark = #{remark}, handle_status = #{handleStatus}, updater_account = #{updaterAccount}, update_date = #{updateDate}, confirmer_account = #{confirmerAccount}, confirm_date = #{confirmDate},
where category_plan_sid = #{categoryPlanSid}

update.foreach:
update s_dev_category_plan
client_id = #{o.clientId}, category_plan_code = #{o.categoryPlanCode}, year = #{o.year}, company_sid = #{o.companySid}, company_code = #{o.companyCode}, brand_code = #{o.brandCode}, product_season_sid = #{o.productSeasonSid}, cancel_remark = #{o.cancelRemark}, remark = #{o.remark}, handle_status = #{o.handleStatus}, updater_account = #{o.updaterAccount}, update_date = #{o.updateDate}, confirmer_account = #{o.confirmerAccount}, confirm_date = #{o.confirmDate},
where category_plan_sid = #{o.categoryPlanSid}
