mapper.update:
update s_dev_develop_plan
client_id = #{clientId}, develop_plan_code = #{developPlanCode}, develop_plan_name = #{developPlanName}, develop_level = #{developLevel}, develop_type = #{developType}, leader_sid = #{leaderSid}, leader_code = #{leaderCode}, product_season_sid = #{productSeasonSid}, product_season_code = #{productSeasonCode}, cost_target = #{costTarget}, retail_price_target = #{retailPriceTarget}, mark_up_rate = #{markUpRate}, category_plan_sid = #{categoryPlanSid}, category_plan_code = #{categoryPlanCode}, category_plan_item_sid = #{categoryPlanItemSid}, category_plan_item_num = #{categoryPlanItemNum}, product_sid_initial = #{productSidInitial}, product_code_initial = #{productCodeInitial}, sample_code_initial = #{sampleCodeInitial}, yearmonth = #{yearmonth}, currency = #{currency}, currency_unit = #{currencyUnit}, handle_status = #{handleStatus}, cancel_remark = #{cancelRemark}, remark = #{remark}, updater_account = #{updaterAccount}, update_date = #{updateDate}, confirmer_account = #{confirmerAccount}, confirm_date = #{confirmDate},
where develop_plan_sid = #{developPlanSid}

update.foreach:
update s_dev_develop_plan
client_id = #{o.clientId}, develop_plan_code = #{o.developPlanCode}, develop_plan_name = #{o.developPlanName}, develop_level = #{o.developLevel}, develop_type = #{o.developType}, leader_sid = #{o.leaderSid}, leader_code = #{o.leaderCode}, product_season_sid = #{o.productSeasonSid}, product_season_code = #{o.productSeasonCode}, cost_target = #{o.costTarget}, retail_price_target = #{o.retailPriceTarget}, mark_up_rate = #{o.markUpRate}, category_plan_sid = #{o.categoryPlanSid}, category_plan_code = #{o.categoryPlanCode}, category_plan_item_sid = #{o.categoryPlanItemSid}, category_plan_item_num = #{o.categoryPlanItemNum}, product_sid_initial = #{o.productSidInitial}, product_code_initial = #{o.productCodeInitial}, sample_code_initial = #{o.sampleCodeInitial}, yearmonth = #{o.yearmonth}, currency = #{o.currency}, currency_unit = #{o.currencyUnit}, handle_status = #{o.handleStatus}, cancel_remark = #{o.cancelRemark}, remark = #{o.remark}, updater_account = #{o.updaterAccount}, update_date = #{o.updateDate}, confirmer_account = #{o.confirmerAccount}, confirm_date = #{o.confirmDate},
where develop_plan_sid = #{o.developPlanSid}

mapper.select:
SELECT t.project_sid, t.project_code, t.project_name, t.product_code, t.material_barcode_code, t.project_status, t.project_type, t.market_region, t.project_leader_sid, t.project_leader_code, t.yearmonth_project, t.plan_start_date, t.plan_end_date, t.plan_type, t.year, t.category_plan_sid, t.category_plan_code, t.actual_end_date, t.sample_sid, t.sample_code, t.handle_status, t.develop_plan_sid, t.develop_plan_code, t1.product_sid_initial, t1.product_code_initial, t1.develop_plan_name, t1.develop_type, t1.leader_sid, t1.leader_code, t1.develop_level, t1.yearmonth, t3.big_class_sid, t3.big_class_code, t3.middle_class_sid, t3.middle_class_code, t3.small_class_sid, t3.small_class_code, t3.group_type, t4.staff_name as project_leader_name, t5.task_name AS project_task_name, t6.handle_status as handle_status_review_zs, t6.confirm_date as confirm_date_review_zs, t6.review_result, t7.product_season_name, t8.node_name as big_class_name, t9.node_name as middle_class_name, t10.node_name as small_class_name, t11.product_season_name, if(t14.erp_material_sku_enter_mode_project = 'XZ', t12.erp_material_sku_barcode, t.erp_material_sku_barcode) AS erp_material_sku_barcode, t13.market_region_name, t15.nick_name as leader_name FROM s_prj_project t LEFT JOIN s_dev_develop_plan t1 ON t.develop_plan_sid = t1.develop_plan_sid LEFT JOIN s_dev_category_plan t2 ON t1.category_plan_sid = t2.category_plan_sid LEFT JOIN s_dev_category_plan_item t3 ON t1.category_plan_item_sid = t3.category_plan_item_sid LEFT JOIN s_bas_staff t4 ON t.project_leader_sid = t4.staff_sid LEFT JOIN ( SELECT t.project_sid, GROUP_CONCAT(t1.task_name order by t1.task_name SEPARATOR ';') as task_name FROM s_prj_project_task t LEFT JOIN s_prj_task t1 ON t.task_sid = t1.task_sid WHERE t.task_status = 'JXZ' GROUP BY t.project_sid ) t5 ON t.project_sid = t5.project_sid LEFT JOIN s_frm_sample_review t6 ON t.project_sid = t6.project_sid AND t6.review_phase = 'ZS' LEFT JOIN s_bas_product_season t7 ON t.product_season_sid = t7.product_season_sid LEFT JOIN s_con_material_class t8 ON t3.big_class_sid = t8.material_class_sid LEFT JOIN s_con_material_class t9 ON t3.middle_class_sid = t9.material_class_sid LEFT JOIN s_con_material_class t10 ON t3.small_class_sid = t10.material_class_sid LEFT JOIN s_bas_product_season t11 ON t.product_season_sid = t11.product_season_sid LEFT JOIN s_bas_material_barcode t12 ON t.material_barcode_sid = t12.barcode_sid LEFT JOIN ( SELECT ta.project_sid, GROUP_CONCAT(tb.dict_label ORDER BY tb.dict_label ASC SEPARATOR ';') as market_region_name FROM s_prj_project ta LEFT JOIN sys_dict_data tb ON tb.dict_type = 's_market_region' AND (ta.market_region = tb.dict_value or ta.market_region like concat(tb.dict_value, ';%') or ta.market_region like concat('%;', tb.dict_value, ';%') or ta.market_region like concat('%;', tb.dict_value)) GROUP BY ta.project_sid ) t13 ON t.project_sid = t13.project_sid LEFT JOIN s_sys_default_setting_client t14 ON t.client_id = t14.client_id LEFT JOIN sys_user t15 ON t1.client_id = t15.client_id and t1.leader_sid = t15.user_id
and t.client_id = #{clientId}
and (t.creator_account = #{creatorAccount}
or t.project_leader_sid = #{currentUserIsLeaderSid} )
and t.project_name like concat('%', #{projectName}, '%')
and t.product_code like concat('%', #{productCode}, '%')
and t.material_barcode_code like concat('%', #{materialBarcodeCode}, '%')
and if(t14.erp_material_sku_enter_mode_project = 'XZ', t12.erp_material_sku_barcode like concat('%', #{erpMaterialSkuBarcode}, '%'), t.erp_material_sku_barcode like concat('%', #{erpMaterialSkuBarcode}, '%'))
and t.year = #{year}
and t.year in (#{year})
and t.yearmonth_project >= #{yearmonthProjectBegin}
and t.yearmonth_project <= #{yearmonthProjectEnd}
and t.product_season_sid = #{productSeasonSid}
and t.product_season_sid in (#{productSeasonSid})
and t1.yearmonth = #{yearmonth}
and t1.yearmonth >= #{yearmonthDevelopBegin}
and t1.yearmonth <= #{yearmonthDevelopEnd}
and t.plan_type in (#{planType})
and t.plan_type = #{planType}
and t.project_status in (#{projectStatus})
and t.project_status = #{projectStatus}
and t1.develop_type = #{developType}
and t1.develop_type in (#{developType})
and t1.leader_sid = #{leaderSid}
and t1.leader_sid in (#{leaderSid})
and ( t.market_region = #{item} or t.market_region like concat(#{item}, ';%') or t.market_region like concat('%;', #{item}, ';%') or t.market_region like concat('%;', #{item}) )
and t.market_region = #{marketRegion}
and t.project_leader_sid = #{projectLeaderSid}
and t.project_leader_sid in (#{projectLeaderSid})
and t.project_type = #{projectType}
and t.project_type in (#{projectType})
and t.develop_plan_code like concat('%', #{developPlanCode}, '%')
and t.sample_code like concat('%', #{sampleCode}, '%')
and t.plan_start_date = #{planStartDate}
and date_format(t.plan_start_date,'%y%m%d') >= date_format(#{planStartDateBegin},'%y%m%d')
and date_format(t.plan_start_date,'%y%m%d') <= date_format(#{planStartDateEnd},'%y%m%d')
and t.plan_end_date = #{planEndDate}
and date_format(t.plan_end_date,'%y%m%d') >= date_format(#{planEndDateBegin},'%y%m%d')
and date_format(t.plan_end_date,'%y%m%d') <= date_format(#{planEndDateEnd},'%y%m%d')
and t.actual_end_date = #{actualEndDate}
and date_format(t.actual_end_date,'%y%m%d') >= date_format(#{actualEndDateBegin},'%y%m%d')
and date_format(t.actual_end_date,'%y%m%d') <= date_format(#{actualEndDateEnd},'%y%m%d')
and t.handle_status = #{handleStatus}
and t.handle_status in (#{handleStatus})
and t1.develop_level = #{developLevel}
and t1.develop_level in (#{developLevel})
and t3.group_type = #{groupType}
and t3.group_type in ( #{groupType})
and t3.big_class_sid = #{bigClassSid}
and t3.big_class_sid in (#{bigClassSid})
and t3.middle_class_sid = #{middleClassSid}
and t3.middle_class_sid in (#{middleClassSid})
and t3.small_class_sid = #{smallClassSid}
and t3.small_class_sid in (#{smallClassSid})
order by t1.yearmonth desc, t.category_plan_code desc, t.develop_plan_code desc, t.project_code desc
