mapper.select:



        and t.client_id = #{clientId}


        and  t1.refer_doc_code like concat('%', #{bookPayBillCode},'%')


        and t.pay_bill_sid = #{payBillSid}


        and t.pay_bill_code like concat('%', #{payBillCode},'%')


        and t.vendor_sid = #{vendorSid}


        and t.vendor_code = #{vendorCode}


        and t.vendor_sid in
        (#{item})


        and t.company_sid = #{companySid}


        and t.company_code = #{companyCode}


        and t.company_sid in
        (#{item})


        and t.book_payment_sid = #{bookPaymentSid}


        and t.book_payment_code like concat('%', #{bookPaymentCode},'%')


        and t.book_payment_item_sid = #{bookPaymentItemSid}


        and t.shengxiao_status = #{shengxiaoStatus}


        and t.creator_account = #{creatorAccount}


        and date_format(t.create_date,'%y%m%d') >= date_format(#{beginTime},'%y%m%d')


        and date_format(t.create_date,'%y%m%d') <= date_format(#{endTime},'%y%m%d')


        and t.data_source_sys = #{dataSourceSys}


        ${params.dataScope}


ORDER BY t.create_date DESC, t1.refer_doc_code DESC
