mapper.select:



        and t.client_id = #{clientId}


        and t8.inventory_document_code like concat('%', #{inventoryDocumentCode},'%')


        and t4.material_code like concat('%', #{materialCode},'%')


        and t4.material_name like concat('%', #{materialName},'%')


        and t5.sku_name like concat('%',#{sku1Name},'%')


        and t6.sku_name like concat('%',#{sku2Name},'%')


        and t.receivable_bill_sid = #{receivableBillSid}


        and t.receivable_bill_code like concat('%', #{receivableBillCode},'%')


        and t.customer_sid = #{customerSid}


        and t.customer_code = #{customerCode}


        and t.customer_sid in
        (#{item})


        and t.company_sid = #{companySid}


        and t.company_code = #{companyCode}


        and t.company_sid in
        (#{item})


        and t.book_receipt_estimation_sid = #{bookReceiptEstimationSid}


        and t.book_receipt_estimation_code like concat('%',#{bookReceiptEstimationCode},'%')


        and t.is_tuihuo = #{isTuihuo}


        and t.book_receipt_estimation_item_sid = #{bookReceiptEstimationItemSid}


        and t.shengxiao_status = #{shengxiaoStatus}


        and t.creator_account = #{creatorAccount}


        and date_format(t.create_date,'%y%m%d') >= date_format(#{beginTime},'%y%m%d')


        and date_format(t.create_date,'%y%m%d') <= date_format(#{endTime},'%y%m%d')


        and t.data_source_sys = #{dataSourceSys}


        ${params.dataScope}


ORDER BY t.create_date DESC, t8.inventory_document_code DESC
