mapper.select:



       and t12.clear_status in
       ( #{clearStatus})


       and t1.sale_person in
       ( #{salePerson})


       and t1.material_type in
       ( #{materialType})


       and t1.company_sid in
       ( #{companySid})


       and t1.product_season_sid in
       ( #{productSeasonSid})


       and t1.customer_sid in
       ( #{customerSid})


       and t1.handle_status in
       ( #{handleStatus})


