       and t1.material_type =#{materialType}


       and t1.sale_person =#{salePerson}


       and t1.company_sid =#{companySid}


       and t1.product_season_sid =#{productSeasonSid}


       and t1.customer_sid =#{customerSid}


       and t1.adjust_bill_code like concat('%', #{adjustBillCode}, '%')


       and ( t.client_id like concat('%',
       #{item}, '%')
   )


       and t.adjust_bill_sid =#{adjustBillSid}


       and ( t.adjust_type like concat('%',
       #{item}, '%')
   )


       and t.currency_amount_tax = #{currencyAmountTax}


       and t.sale_contract_sid =#{saleContractSid}


       and t.sale_contract_code =#{saleContractCode}


       and t.sales_order_sid =#{salesOrderSid}


       and t.sales_order_code =#{salesOrderCode}


       and t.delivery_note_sid =#{deliveryNoteSid}


       and t.delivery_note_code =#{deliveryNoteCode}


       and ( t11.nick_name like concat('%',
       #{item}, '%')
   )


       and t.sale_invoice_sid =#{saleInvoiceSid}


       and t.sale_invoice_code =#{saleInvoiceCode}


       and date_format(t.create_date,'%y%m%d') >= date_format(#{beginTime},'%y%m%d')


       and date_format(t.create_date,'%y%m%d') <= date_format(#{endTime},'%y%m%d')


       and ( t.updater_account like concat('%',
       #{item}, '%')
   )


       and t.update_date = #{updateDate}


       and ( t.data_source_sys like concat('%',
       #{item}, '%')
   )


       ${params.dataScope}

order by t.create_date desc

