mapper.select:



       and t4.cash_pledge_bill_code like concat('%',#{cashPledgeBillCode}, '%')

   and t.cash_pledge_bill_sid in
       ( #{cashPledgeBillSid})


       and t.return_status in
       (#{returnStatus})


       and t.return_status != #{returnStatusNot}


       and t4.handle_status =#{handleStatus}


       and t4.handle_status in
       (#{handleStatus})


       and t4.document_type in
       (#{documentType})


       and t4.document_type =#{documentType}


       and t4.customer_sid in
       (#{customerSid})


       and t4.company_sid in
       (#{companySid})


       and t4.customer_sid =#{customerSid}


       and t4.company_sid =#{companySid}


       and t.client_id = #{clientId}


       and t.cash_pledge_bill_sid =#{cashPledgeBillSid}


       and t.pledge_type = #{pledgeType}


       and t.currency_amount = #{currencyAmount}


       and t.currency_amount_yth = #{currencyAmountYth}


       and t.currency_amount_thz = #{currencyAmountThz}


       and t.return_status = #{returnStatus}


       and t.pre_cash_pledge_bill_sid =#{preCashPledgeBillSid}


       and t.pre_cash_pledge_bill_code =#{preCashPledgeBillCode}


       and t.item_num =#{itemNum}


       and t.sale_contract_sid =#{saleContractSid}


       and (t.sale_contract_code
       like concat('%',
       #{item}, '%')
   )


       and t.sales_order_sid =#{salesOrderSid}


       and t.sales_order_code =#{salesOrderCode}


       and t.delivery_note_sid =#{deliveryNoteSid}


       and t.delivery_note_code =#{deliveryNoteCode}


       and t.paper_contract_code like concat('%', #{paperContractCode}, '%')


       and (t2.nick_name like
       concat('%',
       #{item}, '%')
   )


       and date_format(t.create_date,'%y%m%d') >= date_format(#{beginTime},'%y%m%d')


       and date_format(t.create_date,'%y%m%d') <= date_format(#{endTime},'%y%m%d')


       and t.data_source_sys = #{dataSourceSys}


       ${params.dataScope}

order by t4.cash_pledge_bill_code desc, t.create_date desc

mapper.update:
update s_fin_customer_cash_pledge_bill_item
pledge_type = #{pledgeType}, currency_amount = #{currencyAmount}, currency_amount_yth = #{currencyAmountYth}, currency_amount_thz = #{currencyAmountThz}, return_status = #{returnStatus}, pre_cash_pledge_bill_sid = #{preCashPledgeBillSid}, pre_cash_pledge_bill_code = #{preCashPledgeBillCode}, pre_cash_pledge_bill_item_sid = #{preCashPledgeBillItemSid}, item_num = #{itemNum}, sale_contract_sid = #{saleContractSid}, sale_contract_code = #{saleContractCode}, sales_order_sid = #{salesOrderSid}, sales_order_code = #{salesOrderCode}, delivery_note_sid = #{deliveryNoteSid}, delivery_note_code = #{deliveryNoteCode}, paper_contract_code = #{paperContractCode}, remark = #{remark}, updater_account = #{updaterAccount}, update_date = #{updateDate}, data_source_sys = #{dataSourceSys},
where cash_pledge_bill_item_sid = #{cashPledgeBillItemSid}

update.foreach:
update s_fin_customer_cash_pledge_bill_item
pledge_type = #{o.pledgeType}, currency_amount = #{o.currencyAmount}, currency_amount_yth = #{o.currencyAmountYth}, currency_amount_thz = #{o.currencyAmountThz}, return_status = #{o.returnStatus}, pre_cash_pledge_bill_sid = #{o.preCashPledgeBillSid}, pre_cash_pledge_bill_code = #{o.preCashPledgeBillCode}, pre_cash_pledge_bill_item_sid = #{o.preCashPledgeBillItemSid}, item_num = #{o.itemNum}, sale_contract_sid = #{o.saleContractSid}, sale_contract_code = #{o.saleContractCode}, sales_order_sid = #{o.salesOrderSid}, sales_order_code = #{o.salesOrderCode}, delivery_note_sid = #{o.deliveryNoteSid}, delivery_note_code = #{o.deliveryNoteCode}, paper_contract_code = #{o.paperContractCode}, remark = #{o.remark}, updater_account = #{o.updaterAccount}, update_date = #{o.updateDate}, data_source_sys = #{o.dataSourceSys},
where cash_pledge_bill_item_sid = #{o.cashPledgeBillItemSid}
