mapper.select:



       and t10.approval_user_id in (#{approvalUserId})


       and t10.approval_user_id = #{approvalUserId}


       and t.client_id = #{clientId}


       and t.return_status in
       (#{returnStatus})


       and t.handle_status in
       (#{handleStatus})


       and t.sale_person in
       (#{salePerson})


       and t.customer_sid in
       (#{customerSid})


       and t.company_sid in
       (#{companySid})


       and t.product_season_sid in
       (
       #{productSeasonSid})


       and t.cash_pledge_bill_sid in
       (#{item})


       and t.cash_pledge_bill_sid =#{cashPledgeBillSid}


       and t.cash_pledge_bill_code like concat('%',#{cashPledgeBillCode}, '%')


       and t.customer_sid =#{customerSid}


       and t.company_sid =#{companySid}


       and t.document_date = #{documentDate}


       and t.document_type = #{documentType}


       and t.business_type = #{businessType}


       and t.business_type in
       (#{item})


       and t.company_brand_sid =#{companyBrandSid}


       and t.business_channel = #{businessChannel}


       and t.product_season_sid =#{productSeasonSid}


       and t.sale_org = #{saleOrg}


       and t.sale_person = #{salePerson}


       and t.material_type = #{materialType}


       and t.return_status = #{returnStatus}


       and t.handle_status = #{handleStatus}


       and (t2.nick_name like
       concat('%',
       #{item}, '%')
   )


       and date_format(t.create_date,'%y%m%d') >= date_format(#{beginTime},'%y%m%d')


       and date_format(t.create_date,'%y%m%d') <= date_format(#{endTime},'%y%m%d')


       and t.data_source_sys = #{dataSourceSys}


       ${params.dataScope}

order by t.cash_pledge_bill_code desc

