mapper.select:



        and t.client_id = #{clientId}


        and t.customer_invoice_record_sid = #{customerInvoiceRecordSid}


        and t.customer_invoice_record_sid in
        (#{item})


        and t.customer_invoice_record_code = #{customerInvoiceRecordCode}


        and t.customer_sid = #{customerSid}


        and t.customer_sid in
        (#{item})


        and t.customer_code = #{customerCode}


        and t.company_sid = #{companySid}


        and t.company_sid in
        (#{item})


        and t.company_code = #{companyCode}


        and t.invoice_type = #{invoiceType}


        and t.invoice_type in
        (#{item})


        and t.invoice_category = #{invoiceCategory}


        and t.invoice_category in
        (#{item})


        and t.material_type = #{materialType}


        and t.material_type in
        (#{item})


        and t.product_season_sid = #{productSeasonSid}


        and t.product_season_sid in
        (#{item})


        and t.invoice_num like concat('%', #{invoiceNum}, '%')


        and t.inovice_code like concat('%', #{inoviceCode}, '%')


        and t.send_flag = #{sendFlag}


        and t.send_flag in
        (#{item})


        and t.general_ledger_date = #{generalLedgerDate}


        and date_format(t.general_ledger_date,'%y%m%d') >= date_format(#{generalLedgerDateBegin},'%y%m%d')


        and date_format(t.general_ledger_date,'%y%m%d') <= date_format(#{generalLedgerDateEnd},'%y%m%d')


        and t.invoice_date = #{invoiceDate}


        and date_format(t.invoice_date,'%y%m%d') >= date_format(#{invoiceDateBegin},'%y%m%d')


        and date_format(t.invoice_date,'%y%m%d') <= date_format(#{invoiceDateEnd},'%y%m%d')


        and t.refer_invoice_record_sid = #{referInvoiceRecordSid}


        and t.refer_invoice_record_code like concat('%', #{referInvoiceRecordCode}, '%')


        and t.paper_contract_code like concat('%', #{paperContractCode}, '%')


        and t.shoukuan_status = #{shoukuanStatus}


        and t.shoukuan_status in
        (#{item})


        and t.total_currency_amount_tax = #{totalCurrencyAmountTax}


        and t.total_currency_amount = #{totalCurrencyAmount}


        and t.total_tax_amount = #{totalTaxAmount}


        and t.tax_rate = #{taxRate}


        and t.currency = #{currency}


        and t.currency_unit = #{currencyUnit}


        and t.cancel_remark = #{cancelRemark}


        and t.hongchong_remark = #{hongchongRemark}


        and t.handle_status = #{handleStatus}


        and t.handle_status in
        (#{item})


        and t.creator_account = #{creatorAccount}


        and u1.nick_name like concat('%', #{creatorAccountName}, '%')


        and date_format(t.create_date,'%y%m%d') >= date_format(#{beginTime},'%y%m%d')


        and date_format(t.create_date,'%y%m%d') <= date_format(#{endTime},'%y%m%d')


        and t.data_source_sys = #{dataSourceSys}


        ${params.dataScope}


order by t.customer_invoice_record_code desc , t.create_date desc

mapper.update:
update s_fin_customer_invoice_record
customer_sid = #{customerSid}, customer_code = #{customerCode}, company_sid = #{companySid}, company_code = #{companyCode}, invoice_type = #{invoiceType}, invoice_category = #{invoiceCategory}, material_type = #{materialType}, product_season_sid = #{productSeasonSid}, invoice_num = #{invoiceNum}, inovice_code = #{inoviceCode}, send_flag = #{sendFlag}, general_ledger_date = #{generalLedgerDate}, invoice_date = #{invoiceDate}, business_verify_period = #{businessVerifyPeriod}, refer_invoice_record_sid = #{referInvoiceRecordSid}, refer_invoice_record_code = #{referInvoiceRecordCode}, paper_contract_code = #{paperContractCode}, shoukuan_status = #{shoukuanStatus}, total_currency_amount_tax = #{totalCurrencyAmountTax}, total_currency_amount = #{totalCurrencyAmount}, total_tax_amount = #{totalTaxAmount}, tax_rate = #{taxRate}, currency = #{currency}, currency_unit = #{currencyUnit}, new_receivable_use_date = #{newReceivableUseDate}, cancel_remark = #{cancelRemark}, hongchong_remark = #{hongchongRemark}, remark = #{remark}, handle_status = #{handleStatus}, updater_account = #{updaterAccount}, update_date = #{updateDate}, confirmer_account = #{confirmerAccount}, confirm_date = #{confirmDate},
where customer_invoice_record_sid = #{customerInvoiceRecordSid}

update.foreach:
update s_fin_customer_invoice_record
customer_sid = #{o.customerSid}, customer_code = #{o.customerCode}, company_sid = #{o.companySid}, company_code = #{o.companyCode}, invoice_type = #{o.invoiceType}, invoice_category = #{o.invoiceCategory}, material_type = #{o.materialType}, product_season_sid = #{o.productSeasonSid}, invoice_num = #{o.invoiceNum}, inovice_code = #{o.inoviceCode}, send_flag = #{o.sendFlag}, general_ledger_date = #{o.generalLedgerDate}, invoice_date = #{o.invoiceDate}, business_verify_period = #{o.businessVerifyPeriod}, refer_invoice_record_sid = #{o.referInvoiceRecordSid}, refer_invoice_record_code = #{o.referInvoiceRecordCode}, paper_contract_code = #{o.paperContractCode}, shoukuan_status = #{o.shoukuanStatus}, total_currency_amount_tax = #{o.totalCurrencyAmountTax}, total_currency_amount = #{o.totalCurrencyAmount}, total_tax_amount = #{o.totalTaxAmount}, tax_rate = #{o.taxRate}, currency = #{o.currency}, currency_unit = #{o.currencyUnit}, new_receivable_use_date = #{o.newReceivableUseDate}, cancel_remark = #{o.cancelRemark}, hongchong_remark = #{o.hongchongRemark}, remark = #{o.remark}, handle_status = #{o.handleStatus}, updater_account = #{o.updaterAccount}, update_date = #{o.updateDate}, confirmer_account = #{o.confirmerAccount}, confirm_date = #{o.confirmDate},
where customer_invoice_record_sid = #{o.customerInvoiceRecordSid}
