mapper.select:



        and t.client_id = #{clientId}


        and t.pay_bill_item_invoice_sid = #{payBillItemInvoiceSid}


        and t.pay_bill_item_invoice_sid in
        (#{item})


        and t.pay_bill_sid = #{payBillSid}


        and t1.pay_bill_code like concat('%', #{payBillCode}, '%')


        and t.pay_bill_sid in (#{item})


        and t.vendor_invoice_record_sid = #{vendorInvoiceRecordSid}


        and t.vendor_invoice_record_sid in (#{item})


        and t.vendor_invoice_record_code like concat('%', #{vendorInvoiceRecordCode}, '%')


        and t.currency_amount_tax = #{currencyAmountTax}


        and t.creator_account = #{creatorAccount}


        and date_format(t.create_date,'%y%m%d') >= date_format(#{beginTime},'%y%m%d')


        and date_format(t.create_date,'%y%m%d') <= date_format(#{endTime},'%y%m%d')


        and t1.handle_status = #{billHandleStatus}


        and t2.fukuan_status = #{fukuanStatus}


        and t2.vendor_sid in (#{item})


        and t2.company_sid in (#{item})


        and u3.nick_name like concat('%', #{agentName}, '%')


        and t.data_source_sys = #{dataSourceSys}


        ${params.dataScope}


order by t.create_date desc, t.vendor_invoice_record_code desc

mapper.update:
update s_fin_pay_bill_item_invoice
pay_bill_sid = #{payBillSid}, vendor_invoice_record_sid = #{vendorInvoiceRecordSid}, vendor_invoice_record_code = #{vendorInvoiceRecordCode}, currency_amount_tax = #{currencyAmountTax}, remark = #{remark}, updater_account = #{updaterAccount}, update_date = #{updateDate},
where pay_bill_item_invoice_sid = #{payBillItemInvoiceSid}

update.foreach:
update s_fin_pay_bill_item_invoice
pay_bill_sid = #{o.payBillSid}, vendor_invoice_record_sid = #{o.vendorInvoiceRecordSid}, vendor_invoice_record_code = #{o.vendorInvoiceRecordCode}, currency_amount_tax = #{o.currencyAmountTax}, remark = #{o.remark}, updater_account = #{o.updaterAccount}, update_date = #{o.updateDate},
where pay_bill_item_invoice_sid = #{o.payBillItemInvoiceSid}
