mapper.select:



        and t.client_id = #{clientId}


        and t.pay_bill_item_koukuan_sid = #{payBillItemKoukuanSid}


        and t.pay_bill_item_koukuan_sid in
        (#{item})


        and t.pay_bill_sid = #{payBillSid}


        and t.pay_bill_sid in
        (#{item})


        and t.book_deduction_sid = #{bookDeductionSid}


        and t.book_deduction_sid in (#{item})


        and  t.book_deduction_code like concat('%', #{bookDeductionCode},'%')


        and t.currency_amount_tax = #{currencyAmountTax}


        and  t1.pay_bill_code like concat('%', #{payBillCode},'%')


        and t1.handle_status = #{handleStatus}


        and t1.handle_status in
        (#{item})


        and t1.agent = #{agent}


        and t1.agent in
        (#{item})


        and u3.nick_name like concat('%', #{agentName},'%')


        and t2.refer_doc_code like concat('%', #{referDocCode},'%')


        and t2.clear_status = #{clearStatus}


        and t2.clear_status in
        (#{item})


        and t3.company_sid = #{companySid}


        and t3.company_sid in
        (#{item})


        and t4.vendor_sid = #{vendorSid}


        and t4.vendor_sid in
        (#{item})


        and t.creator_account = #{creatorAccount}


        and date_format(t.create_date,'%y%m%d') >= date_format(#{beginTime},'%y%m%d')


        and date_format(t.create_date,'%y%m%d') <= date_format(#{endTime},'%y%m%d')


        and t.updater_account = #{updaterAccount}


        and t.update_date = #{updateDate}


        and t.data_source_sys = #{dataSourceSys}


        ${params.dataScope}


order by t.create_date desc, t2.refer_doc_code desc

mapper.update:
update s_fin_pay_bill_item_koukuan
currency_amount_tax = #{currencyAmountTax}, remark = #{remark}, updater_account = #{updaterAccount}, update_date = #{updateDate},
where pay_bill_item_koukuan_sid = #{payBillItemKoukuanSid}

update.foreach:
update s_fin_pay_bill_item_koukuan
remark = #{o.remark}, updater_account = #{o.updaterAccount}, update_date = #{o.updateDate},
where pay_bill_item_koukuan_sid = #{o.payBillItemKoukuanSid}
