mapper.select:



        and t.client_id = #{clientId}


        and t.pay_bill_sid = #{payBillSid}


        and t.pay_bill_sid in
        (#{item})


        and t1.pay_bill_code like concat('%', #{payBillCode}, '%')


        and t1.vendor_sid in (#{item})


        and t1.company_sid in (#{item})


        and t1.business_type in (#{item})


        and t1.document_type in (#{item})


        and t1.payment_status = #{paymentStatus}


        and t1.payment_status in (#{item})


        and t1.handle_status = #{handleStatus}


        and t1.handle_status in (#{item})


        and t.account_document_sid = #{accountDocumentSid}


        and t.account_document_code = #{accountDocumentCode}


        and t.product_season_sid = #{productSeasonSid}


        and t.product_season_sid in (#{item})


        and t.book_type = #{bookType}


        and t.book_source_category = #{bookSourceCategory}


        and t.account_item_sid = #{accountItemSid}


        and t.account_item_code = #{accountItemCode}


        and t.currency_amount_tax = #{currencyAmountTax}


        and t.payment_method = #{paymentMethod}


        and t.payment_method in (#{item})


        and t.account_category = #{accountCategory}


        and t.account_category in (#{item})


        and t.paper_contract_code = #{paperContractCode}


        and t.tax_rate = #{taxRate}


        and t.purchase_invoice_sid = #{purchaseInvoiceSid}


        and t.purchase_invoice_code = #{purchaseInvoiceCode}


        and t.purchase_order_sid = #{purchaseOrderSid}


        and t.purchase_order_code = #{purchaseOrderCode}


        and t.purchase_contract_sid = #{purchaseContractSid}


        and t.purchase_contract_code = #{purchaseContractCode}


        and t.item_num = #{itemNum}


        and t.creator_account = #{creatorAccount}


        and date_format(t.create_date,'%y%m%d') >= date_format(#{beginTime},'%y%m%d')


        and date_format(t.create_date,'%y%m%d') <= date_format(#{endTime},'%y%m%d')


        and t.updater_account = #{updaterAccount}


        and t.update_date = #{updateDate}


        and t.data_source_sys = #{dataSourceSys}


        ${params.dataScope}


order by t1.pay_bill_code desc, t.item_num asc

mapper.update:
update s_fin_pay_bill_item
account_document_sid = #{accountDocumentSid}, account_document_code = #{accountDocumentCode}, product_season_sid = #{productSeasonSid}, book_type = #{bookType}, book_source_category = #{bookSourceCategory}, account_item_sid = #{accountItemSid}, account_item_code = #{accountItemCode}, currency_amount_tax = #{currencyAmountTax}, payment_method = #{paymentMethod}, account_category = #{accountCategory}, paper_contract_code = #{paperContractCode}, tax_rate = #{taxRate}, purchase_invoice_sid = #{purchaseInvoiceSid}, purchase_invoice_code = #{purchaseInvoiceCode}, purchase_order_sid = #{purchaseOrderSid}, purchase_order_code = #{purchaseOrderCode}, purchase_contract_sid = #{purchaseContractSid}, purchase_contract_code = #{purchaseContractCode}, item_num = #{itemNum}, remark = #{remark}, updater_account = #{updaterAccount}, update_date = #{updateDate},
where pay_bill_item_sid = #{payBillItemSid}

update.foreach:
update s_fin_pay_bill_item
account_document_sid = #{o.accountDocumentSid}, account_document_code = #{o.accountDocumentCode}, product_season_sid = #{o.productSeasonSid}, book_type = #{o.bookType}, book_source_category = #{o.bookSourceCategory}, account_item_sid = #{o.accountItemSid}, account_item_code = #{o.accountItemCode}, currency_amount_tax = #{o.currencyAmountTax}, payment_method = #{o.paymentMethod}, account_category = #{o.accountCategory}, paper_contract_code = #{o.paperContractCode}, tax_rate = #{o.taxRate}, purchase_invoice_sid = #{o.purchaseInvoiceSid}, purchase_invoice_code = #{o.purchaseInvoiceCode}, purchase_order_sid = #{o.purchaseOrderSid}, purchase_order_code = #{o.purchaseOrderCode}, purchase_contract_sid = #{o.purchaseContractSid}, purchase_contract_code = #{o.purchaseContractCode}, item_num = #{o.itemNum}, remark = #{o.remark}, updater_account = #{o.updaterAccount}, update_date = #{o.updateDate},
where pay_bill_item_sid = #{o.payBillItemSid}
