mapper.update:
update s_fin_pay_bill_item_outsource_settle
currency_amount_tax = #{currencyAmountTax}, remark = #{remark}, updater_account = #{updaterAccount}, update_date = #{updateDate},
where pay_bill_item_outsource_settle_sid = #{payBillItemOutsourceSettleSid}

update.foreach:
update s_fin_pay_bill_item_outsource_settle
currency_amount_tax = #{o.currencyAmountTax}, remark = #{o.remark}, updater_account = #{o.updaterAccount}, update_date = #{o.updateDate},
where pay_bill_item_outsource_settle_sid = #{o.payBillItemOutsourceSettleSid}
