mapper.select:



        and t.client_id = #{clientId}


        and t.receivable_bill_item_invoice_sid = #{receivableBillItemInvoiceSid}


        and t.receivable_bill_item_invoice_sid in (#{item})


        and t.receivable_bill_sid = #{receivableBillSid}


        and t.receivable_bill_sid in (#{item})


        and t1.receivable_bill_code like concat('%', #{receivableBillCode}, '%')


        and t.customer_invoice_record_sid = #{customerInvoiceRecordSid}


        and t.customer_invoice_record_sid in (#{item})


        and t.customer_invoice_record_code like concat('%', #{customerInvoiceRecordCode}, '%')


        and t.currency_amount_tax = #{currencyAmountTax}


        and t.creator_account = #{creatorAccount}


        and date_format(t.create_date,'%y%m%d') >= date_format(#{beginTime},'%y%m%d')


        and date_format(t.create_date,'%y%m%d') <= date_format(#{endTime},'%y%m%d')


        and t1.handle_status = #{billHandleStatus}


        and t2.shoukuan_status = #{shoukuanStatus}


        and t2.customer_sid in (#{item})


        and t2.company_sid in (#{item})


        and u3.nick_name like concat('%', #{agentName}, '%')


        and t.data_source_sys = #{dataSourceSys}


        ${params.dataScope}


order by t.create_date desc, t.customer_invoice_record_code desc

mapper.update:
update s_fin_receivable_bill_item_invoice
receivable_bill_sid = #{receivableBillSid}, customer_invoice_record_sid = #{customerInvoiceRecordSid}, customer_invoice_record_code = #{customerInvoiceRecordCode}, currency_amount_tax = #{currencyAmountTax}, remark = #{remark}, updater_account = #{updaterAccount}, update_date = #{updateDate},
where receivable_bill_item_invoice_sid = #{receivableBillItemInvoiceSid}

update.foreach:
update s_fin_receivable_bill_item_invoice
receivable_bill_sid = #{o.receivableBillSid}, customer_invoice_record_sid = #{o.customerInvoiceRecordSid}, customer_invoice_record_code = #{o.customerInvoiceRecordCode}, currency_amount_tax = #{o.currencyAmountTax}, remark = #{o.remark}, updater_account = #{o.updaterAccount}, update_date = #{o.updateDate},
where receivable_bill_item_invoice_sid = #{o.receivableBillItemInvoiceSid}
