mapper.select:



        and t.client_id = #{clientId}


        and t.receivable_bill_item_sid = #{receivableBillItemSid}


        and t.receivable_bill_item_sid in (#{item})


        and t.receivable_bill_sid = #{receivableBillSid}


        and t.receivable_bill_sid in (#{item})


        and t1.receivable_bill_code like concat('%', #{receivableBillCode}, '%')


        and t1.customer_sid in (#{item})


        and t1.company_sid in (#{item})


        and t1.business_type in (#{item})


        and t1.document_type in (#{item})


        and t1.receipt_payment_status = #{receiptPaymentStatus}


        and t1.receipt_payment_status in (#{item})


        and t1.handle_status = #{handleStatus}


        and t1.handle_status in (#{item})


        and t.account_category = #{accountCategory}


        and t.account_category in (#{item})


        and t.account_document_sid = #{accountDocumentSid}


        and t.account_document_code = #{accountDocumentCode}


        and t.product_season_sid = #{productSeasonSid}


        and t.product_season_sid in (#{item})


        and t.book_type = #{bookType}


        and t.book_source_category = #{bookSourceCategory}


        and t.account_item_sid = #{accountItemSid}


        and t.account_item_code = #{accountItemCode}


        and t.currency_amount_tax = #{currencyAmountTax}


        and t.payment_method = #{paymentMethod}


        and t.payment_method in (#{item})


        and t.business_channel = #{businessChannel}


        and t.business_channel in (#{item})


        and t.paper_contract_code = #{paperContractCode}


        and t.tax_rate = #{taxRate}


        and t.item_num = #{itemNum}


        and t.sale_invoice_sid = #{saleInvoiceSid}


        and t.sale_invoice_code = #{saleInvoiceCode}


        and t.sales_order_sid = #{salesOrderSid}


        and t.sales_order_code = #{salesOrderCode}


        and t.sale_contract_sid = #{saleContractSid}


        and t.sale_contract_code = #{saleContractCode}


        and t.creator_account = #{creatorAccount}


        and date_format(t.create_date,'%y%m%d') >= date_format(#{beginTime},'%y%m%d')


        and date_format(t.create_date,'%y%m%d') <= date_format(#{endTime},'%y%m%d')


        and t.updater_account = #{updaterAccount}


        and t.update_date = #{updateDate}


        and t.data_source_sys = #{dataSourceSys}


        ${params.dataScope}


order by t1.receivable_bill_code desc, t.item_num asc

mapper.update:
update s_fin_receivable_bill_item
account_document_sid = #{accountDocumentSid}, account_document_code = #{accountDocumentCode}, product_season_sid = #{productSeasonSid}, book_type = #{bookType}, book_source_category = #{bookSourceCategory}, account_item_sid = #{accountItemSid}, account_item_code = #{accountItemCode}, currency_amount_tax = #{currencyAmountTax}, payment_method = #{paymentMethod}, account_category = #{accountCategory}, business_channel = #{businessChannel}, paper_contract_code = #{paperContractCode}, tax_rate = #{taxRate}, item_num = #{itemNum}, sale_invoice_sid = #{saleInvoiceSid}, sale_invoice_code = #{saleInvoiceCode}, sales_order_sid = #{salesOrderSid}, sales_order_code = #{salesOrderCode}, sale_contract_sid = #{saleContractSid}, sale_contract_code = #{saleContractCode}, remark = #{remark}, updater_account = #{updaterAccount}, update_date = #{updateDate},
where receivable_bill_item_sid = #{receivableBillItemSid}

update.foreach:
update s_fin_receivable_bill_item
account_document_sid = #{o.accountDocumentSid}, account_document_code = #{o.accountDocumentCode}, product_season_sid = #{o.productSeasonSid}, book_type = #{o.bookType}, book_source_category = #{o.bookSourceCategory}, account_item_sid = #{o.accountItemSid}, account_item_code = #{o.accountItemCode}, currency_amount_tax = #{o.currencyAmountTax}, payment_method = #{o.paymentMethod}, account_category = #{o.accountCategory}, business_channel = #{o.businessChannel}, paper_contract_code = #{o.paperContractCode}, tax_rate = #{o.taxRate}, item_num = #{o.itemNum}, sale_invoice_sid = #{o.saleInvoiceSid}, sale_invoice_code = #{o.saleInvoiceCode}, sales_order_sid = #{o.salesOrderSid}, sales_order_code = #{o.salesOrderCode}, sale_contract_sid = #{o.saleContractSid}, sale_contract_code = #{o.saleContractCode}, remark = #{o.remark}, updater_account = #{o.updaterAccount}, update_date = #{o.updateDate},
where receivable_bill_item_sid = #{o.receivableBillItemSid}
