mapper.select:



        and t.client_id = #{clientId}


        and t.receivable_bill_item_yushou_sid = #{receivableBillItemYushouSid}


        and t.receivable_bill_item_yushou_sid in (#{item})


        and t.receivable_bill_sid = #{receivableBillSid}


        and t.receivable_bill_sid in (#{item})


        and t.book_receipt_payment_sid = #{bookReceiptPaymentSid}


        and t.book_receipt_payment_sid in (#{item})


        and t.book_receipt_payment_code like concat('%', #{bookReceiptPaymentCode},'%')


        and t.currency_amount_tax = #{currencyAmountTax}


        and t1.handle_status = #{handleStatus}


        and t1.handle_status in
        (#{item})


        and t1.receivable_bill_code like concat('%', #{receivableBillCode},'%')


        and t1.agent = #{agent}


        and t1.agent in
        (#{item})


        and u3.nick_name like concat('%', #{agentName},'%')


        and t2.refer_doc_code like concat('%', #{referDocCode},'%')


        and t2.agent = #{referAgent}


        and t2.agent in
        (#{item})


        and t2.agent_name like concat('%', #{referAgentName},'%')


        and t2.clear_status = #{clearStatus}


        and t2.clear_status in
        (#{item})


        and t3.company_sid = #{companySid}


        and t3.company_sid in
        (#{item})


        and t4.customer_sid = #{customerSid}


        and t4.customer_sid in
        (#{item})


        and t.creator_account = #{creatorAccount}


        and date_format(t.create_date,'%y%m%d') >= date_format(#{beginTime},'%y%m%d')


        and date_format(t.create_date,'%y%m%d') <= date_format(#{endTime},'%y%m%d')


        and t.updater_account = #{updaterAccount}


        and t.update_date = #{updateDate}


        and t.data_source_sys = #{dataSourceSys}


        ${params.dataScope}


order by t.create_date desc, t2.refer_doc_code desc

mapper.update:
update s_fin_receivable_bill_item_yushou
currency_amount_tax = #{currencyAmountTax}, remark = #{remark}, updater_account = #{updaterAccount}, update_date = #{updateDate},
where receivable_bill_item_yushou_sid = #{receivableBillItemYushouSid}

update.foreach:
update s_fin_receivable_bill_item_yushou
currency_amount_tax = #{o.currencyAmountTax}, remark = #{o.remark}, updater_account = #{o.updaterAccount}, update_date = #{o.updateDate},
where receivable_bill_item_yushou_sid = #{o.receivableBillItemYushouSid}
