mapper.select:



       and t12.clear_status in
       ( #{clearStatus})


       and t1.buyer in
       ( #{buyer})


       and t1.material_type in
       ( #{materialType})


       and t1.company_sid in
       ( #{companySid})


       and t1.product_season_sid in
       ( #{productSeasonSid})


       and t1.vendor_sid in
       ( #{vendorSid})


       and t1.handle_status in
       ( #{handleStatus})


       and t1.handle_status =#{handleStatus}


       and t5.clear_status =#{clearStatus}


       and t1.material_type =#{materialType}


       and t1.buyer =#{buyer}


       and t1.company_sid =#{companySid}


       and t1.product_season_sid =#{productSeasonSid}


       and t1.vendor_sid =#{vendorSid}


       and t1.deduction_bill_code like concat('%', #{deductionBillCode}, '%')


       and t.client_id = #{clientId}


       and t.deduction_bill_sid =#{deductionBillSid}


       and t.deduction_type = #{deductionType}


       and t.currency_amount_tax = #{currencyAmountTax}


       and t.item_num =#{itemNum}


       and t.purchase_contract_sid =#{purchaseContractSid}


       and t.purchase_contract_code =#{purchaseContractCode}


       and t.purchase_order_sid =#{purchaseOrderSid}


       and t.purchase_order_code =#{purchaseOrderCode}


       and t.delivery_note_sid =#{deliveryNoteSid}


       and t.delivery_note_code =#{deliveryNoteCode}


       and t.paper_contract_code like concat('%', #{paperContractCode}, '%')


       and (t11.nick_name like
       concat('%',
       #{item}, '%')
   )


       and date_format(t.create_date,'%y%m%d') >= date_format(#{beginTime},'%y%m%d')


       and date_format(t.create_date,'%y%m%d') <= date_format(#{endTime},'%y%m%d')


       and t.data_source_sys = #{dataSourceSys}


       ${params.dataScope}

order by t.create_date desc

mapper.update:
update s_fin_vendor_deduction_bill_item
deduction_type = #{deductionType}, currency_amount_tax = #{currencyAmountTax}, item_num = #{itemNum}, purchase_contract_sid = #{purchaseContractSid}, purchase_contract_code = #{purchaseContractCode}, purchase_order_sid = #{purchaseOrderSid}, purchase_order_code = #{purchaseOrderCode}, delivery_note_sid = #{deliveryNoteSid}, delivery_note_code = #{deliveryNoteCode}, paper_contract_code = #{paperContractCode}, remark = #{remark}, updater_account = #{updaterAccount}, update_date = #{updateDate}, data_source_sys = #{dataSourceSys},
where deduction_bill_item_sid = #{deductionBillItemSid}

update.foreach:
update s_fin_vendor_deduction_bill_item
deduction_type = #{o.deductionType}, currency_amount_tax = #{o.currencyAmountTax}, item_num = #{o.itemNum}, purchase_contract_sid = #{o.purchaseContractSid}, purchase_contract_code = #{o.purchaseContractCode}, purchase_order_sid = #{o.purchaseOrderSid}, purchase_order_code = #{o.purchaseOrderCode}, delivery_note_sid = #{o.deliveryNoteSid}, delivery_note_code = #{o.deliveryNoteCode}, paper_contract_code = #{o.paperContractCode}, remark = #{o.remark}, updater_account = #{o.updaterAccount}, update_date = #{o.updateDate}, data_source_sys = #{o.dataSourceSys},
where deduction_bill_item_sid = #{o.deductionBillItemSid}
