mapper.select:



        and t.client_id = #{clientId}


        and t.vendor_invoice_record_sid = #{vendorInvoiceRecordSid}


        and t.vendor_invoice_record_sid in
        (#{item})


        and t.vendor_invoice_record_code like concat('%', #{vendorInvoiceRecordCode}, '%')


        and t.vendor_sid = #{vendorSid}


        and t.vendor_sid in
        (#{item})


        and t.vendor_code = #{vendorCode}


        and t.company_sid = #{companySid}


        and t.company_sid in
        (#{item})


        and t.company_code = #{companyCode}


        and t.invoice_type = #{invoiceType}


        and t.invoice_type in
        (#{item})


        and t.invoice_category = #{invoiceCategory}


        and t.invoice_category in
        (#{item})


        and t.material_type = #{materialType}


        and t.material_type in
        (#{item})


        and t.product_season_sid = #{productSeasonSid}


        and t.product_season_sid in
        (#{item})


        and t.invoice_num like concat('%', #{invoiceNum}, '%')


        and t.inovice_code like concat('%', #{inoviceCode}, '%')


        and t.sign_status = #{signStatus}


        and t.sign_status in
        (#{item})


        and t.general_ledger_date = #{generalLedgerDate}


        and date_format(t.general_ledger_date,'%y%m%d') >= date_format(#{generalLedgerDateBegin},'%y%m%d')


        and date_format(t.general_ledger_date,'%y%m%d') <= date_format(#{generalLedgerDateEnd},'%y%m%d')


        and t.invoice_date = #{invoiceDate}


        and date_format(t.invoice_date,'%y%m%d') >= date_format(#{invoiceDateBegin},'%y%m%d')


        and date_format(t.invoice_date,'%y%m%d') <= date_format(#{invoiceDateEnd},'%y%m%d')


        and t.refer_invoice_record_sid = #{referInvoiceRecordSid}


        and t.refer_invoice_record_code like concat('%', #{referInvoiceRecordCode}, '%')


        and t.paper_contract_code like concat('%', #{paperContractCode}, '%')


        and t.fukuan_status = #{fukuanStatus}


        and t.fukuan_status in
        (#{item})


        and t.total_currency_amount_tax = #{totalCurrencyAmountTax}


        and t.total_currency_amount = #{totalCurrencyAmount}


        and t.total_tax_amount = #{totalTaxAmount}


        and t.tax_rate = #{taxRate}


        and t.currency = #{currency}


        and t.currency_unit = #{currencyUnit}


        and t.cancel_remark = #{cancelRemark}


        and t.hongchong_remark = #{hongchongRemark}


        and t.handle_status = #{handleStatus}


        and t.handle_status in
        (#{item})


        and t.creator_account = #{creatorAccount}


        and u1.nick_name like concat('%', #{creatorAccountName}, '%')


        and date_format(t.create_date,'%y%m%d') >= date_format(#{beginTime},'%y%m%d')


        and date_format(t.create_date,'%y%m%d') <= date_format(#{endTime},'%y%m%d')


        and t.data_source_sys = #{dataSourceSys}


        ${params.dataScope}


order by t.vendor_invoice_record_code desc , t.create_date desc

