mapper.select:



        and t.client_id = #{clientId}


        and t.arrival_notice_sid = #{arrivalNoticeSid}


        and t.arrival_notice_sid in
        (#{arrivalNoticeSid})


        and t.arrival_notice_code like concat('%', #{arrivalNoticeCode}, '%')


        and t5.product_code like concat('%', #{productCode}, '%')


        and t5.material_barcode_code like concat('%', #{materialBarcodeCode}, '%')


        and if(t11.erp_material_sku_enter_mode_project = 'XZ', t10.erp_material_sku_barcode like concat('%', #{erpMaterialSkuBarcode}, '%'),
            t5.erp_material_sku_barcode like concat('%', #{erpMaterialSkuBarcode}, '%'))


        and t5.develop_plan_sid = #{developPlanSid}


        and t5.develop_plan_code like concat('%', #{developPlanCode}, '%')


        and t6.develop_plan_name like concat('%', #{developPlanName}, '%')


        and t5.sample_sid = #{sampleSid}


        and t5.sample_code like concat('%', #{sampleCode}, '%')


        and t.project_sid = #{projectSid}


        and t.project_code like concat('%', #{projectCode}, '%')


        and t.project_task_sid = #{projectTaskSid}


        and t5.project_name like concat('%', #{projectName}, '%')


        and t.comment = #{comment}


        and t.handle_status = #{handleStatus}


        and t.handle_status in
        (#{handleStatus})


        and t.creator_account = #{creatorAccount}


        and t.creator_account in
        (#{item})


        and t2.nick_name like concat('%', #{creatorAccountName},'%')


        and date_format(t.create_date,'%y%m%d') = date_format(#{createDate},'%y%m%d')


        and date_format(t.create_date,'%y%m%d') >= date_format(#{beginTime},'%y%m%d')


        and date_format(t.create_date,'%y%m%d') <= date_format(#{endTime},'%y%m%d')


        and t.updater_account = #{updaterAccount}


        and t.update_date = #{updateDate}


        and t.confirmer_account = #{confirmerAccount}


        and t.confirm_date = #{confirmDate}


        and t.data_source_sys = #{dataSourceSys}


        ${params.dataScope}


order by t.arrival_notice_code desc

mapper.update:
update s_frm_arrival_notice
client_id = #{clientId}, arrival_notice_code = #{arrivalNoticeCode}, project_sid = #{projectSid}, project_code = #{projectCode}, project_task_sid = #{projectTaskSid}, comment = #{comment}, remark = #{remark}, handle_status = #{handleStatus}, updater_account = #{updaterAccount}, update_date = #{updateDate}, confirmer_account = #{confirmerAccount}, confirm_date = #{confirmDate},
where arrival_notice_sid = #{arrivalNoticeSid}

update.foreach:
update s_frm_arrival_notice
client_id = #{o.clientId}, arrival_notice_code = #{o.arrivalNoticeCode}, project_sid = #{o.projectSid}, project_code = #{o.projectCode}, project_task_sid = #{o.projectTaskSid}, comment = #{o.comment}, remark = #{o.remark}, handle_status = #{o.handleStatus}, updater_account = #{o.updaterAccount}, update_date = #{o.updateDate}, confirmer_account = #{o.confirmerAccount}, confirm_date = #{o.confirmDate},
where arrival_notice_sid = #{o.arrivalNoticeSid}
