        #{photoSampleGainSid})


        and t.photo_sample_gain_code like concat('%', #{photoSampleGainCode},'%')


        and t5.yearmonth_project >= #{yearmonthProjectBegin}


        and t5.yearmonth_project <= #{yearmonthProjectEnd}


        and t5.product_code like concat('%', #{productCode}, '%')


        and t5.material_barcode_code like concat('%', #{materialBarcodeCode}, '%')


        and if(t17.erp_material_sku_enter_mode_project = 'XZ', t16.erp_material_sku_barcode like concat('%', #{erpMaterialSkuBarcode}, '%'),
            t5.erp_material_sku_barcode like concat('%', #{erpMaterialSkuBarcode}, '%'))


        and t5.develop_plan_sid = #{developPlanSid}


        and t5.develop_plan_code like concat('%', #{developPlanCode},'%')


