        t.sale_station = #{item} or t.sale_station like concat(#{item}, ';%')
        or t.sale_station like concat('%;', #{item}, ';%') or t.sale_station like concat('%;', #{item})
    )


        and t.score = #{score}


        and t.operator = #{operator}


        and t.operator in
        (#{operator})


        and t.product_leader = #{productLeader}


        and t.product_leader in
        (#{productLeader})


