        and date_format(t.sample_deliver_date,'%y%m%d') <= date_format(#{sampleDeliverDateEnd},'%y%m%d')


        and t.comment = #{comment}


        and t.handle_status = #{handleStatus}


        and t.handle_status in
        (#{handleStatus})


        and t11.group_type = #{groupType}


        and t11.group_type in
        (
        #{groupType})


        and (t14.approval_user_id = #{approvalUserId}  or t14.approval_user_id like concat('%, ', #{approvalUserId}, ',%')
        or t14.approval_user_id like concat('%, ', #{approvalUserId}) or t14.approval_user_id like concat(#{approvalUserId}, ',%')
        or t14.approval_user_id like concat('%,', #{approvalUserId}, ',%') or t14.approval_user_id like concat('%,', #{approvalUserId}))


