update s_frm_photo_sample_gain
client_id = #{clientId}, photo_sample_gain_code = #{photoSampleGainCode}, project_sid = #{projectSid}, project_code = #{projectCode}, project_task_sid = #{projectTaskSid}, sale_station = #{saleStation}, score = #{score}, operator = #{operator}, product_leader = #{productLeader}, sample_deliver_date = #{sampleDeliverDate}, comment = #{comment}, remark = #{remark}, handle_status = #{handleStatus}, updater_account = #{updaterAccount}, update_date = #{updateDate}, confirmer_account = #{confirmerAccount}, confirm_date = #{confirmDate},
