mapper.select:



        and t.client_id = #{clientId}


        and t.sample_review_sid = #{sampleReviewSid}


        and t.sample_review_sid in
        (#{sampleReviewSid})


        and t.sample_review_code like concat('%', #{sampleReviewCode}, '%')


        and t5.develop_plan_sid = #{developPlanSid}


        and t5.develop_plan_sid in
        (#{developPlanSid})


        and t5.develop_plan_code like concat('%', #{developPlanCode}, '%')


