        and if(t19.erp_material_sku_enter_mode_project = 'XZ', t18.erp_material_sku_barcode like concat('%', #{erpMaterialSkuBarcode}, '%'),
            t5.erp_material_sku_barcode like concat('%', #{erpMaterialSkuBarcode}, '%'))


