        and t.designer_assistant_sid = #{designerAssistantSid}


        and t.designer_assistant_sid in
        (#{designerAssistantSid})


        and t.designer_assistant_code = #{designerAssistantCode}


        and t.specification_size = #{specificationSize}


        and t.packing_size = #{packingSize}


