        #{groupType})


        and (t16.approval_user_id = #{approvalUserId}  or t16.approval_user_id like concat('%, ', #{approvalUserId}, ',%')
        or t16.approval_user_id like concat('%, ', #{approvalUserId}) or t16.approval_user_id like concat(#{approvalUserId}, ',%')
