        and t11.middle_class_sid = #{middleClassSid}


        and t11.middle_class_sid in
        (#{middleClassSid})


        and t11.small_class_sid = #{smallClassSid}


        and t11.small_class_sid in
        (#{smallClassSid})


        ${params.dataScope}


order by t.sample_review_code desc

