mapper.update:
update s_hr_other_personnel_certificate
other_personnel_certificate_code = #{otherPersonnelCertificateCode}, staff_sid = #{staffSid}, staff_code = #{staffCode}, document_type = #{documentType}, esign_id = #{esignId}, esign_status = #{esignStatus}, esign_desc = #{esignDesc}, remark = #{remark}, sign_in_status = #{signInStatus}, esign_end_time = #{esignEndTime}, esign_platform = #{esignPlatform}, handle_status = #{handleStatus}, creator_account = #{creatorAccount}, create_date = #{createDate}, updater_account = #{updaterAccount}, update_date = #{updateDate}, confirmer_account = #{confirmerAccount}, confirm_date = #{confirmDate}
where other_personnel_certificate_sid = #{otherPersonnelCertificateSid}

update.foreach:
update s_hr_other_personnel_certificate
other_personnel_certificate_code = #{o.otherPersonnelCertificateCode}, staff_sid = #{o.staffSid}, staff_code = #{o.staffCode}, document_type = #{o.documentType}, esign_id = #{o.esignId}, esign_status = #{o.esignStatus}, esign_desc = #{o.esignDesc}, remark = #{o.remark}, sign_in_status = #{o.signInStatus}, esign_end_time = #{o.esignEndTime}, esign_platform = #{o.esignPlatform}, handle_status = #{o.handleStatus}, creator_account = #{o.creatorAccount}, create_date = #{o.createDate}, updater_account = #{o.updaterAccount}, update_date = #{o.updateDate}, confirmer_account = #{o.confirmerAccount}, confirm_date = #{o.confirmDate}
where other_personnel_certificate_sid = #{o.otherPersonnelCertificateSid}
