mapper.update:
update s_inv_storehouse_material
material_sid = #{materialSid}, sku1_sid = #{sku1Sid}, sku2_sid = #{sku2Sid}, barcode_sid = #{barcodeSid}, storehouse_sid = #{storehouseSid}, price = #{price}, price_average = #{priceAverage}, price_uniform = #{priceUniform}, usage_frequency_flag = #{usageFrequencyFlag}, latest_purchase_entry_date = #{latestPurchaseEntryDate}, latest_manufact_entry_date = #{latestManufactEntryDate}, latest_transfer_entry_date = #{latestTransferEntryDate}, latest_stock_entry_date = #{latestStockEntryDate}, latest_sale_out_date = #{latestSaleOutDate}, latest_transfer_out_date = #{latestTransferOutDate}, latest_requisition_out_date = #{latestRequisitionOutDate}, latest_stock_out_date = #{latestStockOutDate}, remark = #{remark}, updater_account = #{updaterAccount}, update_date = #{updateDate}, confirmer_account = #{confirmerAccount}, confirm_date = #{confirmDate}, data_source_sys = #{dataSourceSys},
where storehouse_material_sid = #{storehouseMaterialSid}

update.foreach:
update s_inv_storehouse_material
material_sid = #{o.materialSid}, sku1_sid = #{o.sku1Sid}, sku2_sid = #{o.sku2Sid}, barcode_sid = #{o.barcodeSid}, storehouse_sid = #{o.storehouseSid}, price = #{o.price}, price_average = #{o.priceAverage}, price_uniform = #{o.priceUniform}, usage_frequency_flag = #{o.usageFrequencyFlag}, latest_purchase_entry_date = #{o.latestPurchaseEntryDate}, latest_manufact_entry_date = #{o.latestManufactEntryDate}, latest_transfer_entry_date = #{o.latestTransferEntryDate}, latest_stock_entry_date = #{o.latestStockEntryDate}, latest_sale_out_date = #{o.latestSaleOutDate}, latest_transfer_out_date = #{o.latestTransferOutDate}, latest_requisition_out_date = #{o.latestRequisitionOutDate}, latest_stock_out_date = #{o.latestStockOutDate}, remark = #{o.remark}, updater_account = #{o.updaterAccount}, update_date = #{o.updateDate}, confirmer_account = #{o.confirmerAccount}, confirm_date = #{o.confirmDate}, data_source_sys = #{o.dataSourceSys},
where storehouse_material_sid = #{o.storehouseMaterialSid}

update s_inv_storehouse_material
usage_frequency_flag = #{o.usageFrequencyFlag}, updater_account = #{o.updaterAccount}, update_date = #{o.updateDate},
where barcode_sid = #{o.barcodeSid} and storehouse_sid = #{o.storehouseSid}
