mapper.select:



        and t.client_id = #{clientId}


        and t.day_manufacture_plan_item_sid =#{dayManufacturePlanItemSid}


        and t.day_manufacture_plan_sid =#{dayManufacturePlanSid}


        and t.manufacture_order_sid =#{manufactureOrderSid}


        and t.manufacture_order_code like concat('%',#{manufactureOrderCode}, '%')


        and t.material_sid =#{materialSid}


        and t.manufacture_order_process_sid =#{manufactureOrderProcessSid}


        and t.unit_base = #{unitBase}


        and t.plan_quantity = #{planQuantity}


        and t.plan_defective_quantity = #{planDefectiveQuantity}


        and t.item_num =#{itemNum}


        and t.creator_account = #{creatorAccount}


        and date_format(t.create_date,'%y%m%d') >= date_format(#{beginTime},'%y%m%d')


        and date_format(t.create_date,'%y%m%d') <= date_format(#{endTime},'%y%m%d')


        and t.updater_account = #{updaterAccount}


        and t.update_date = #{updateDate}


        and t.data_source_sys = #{dataSourceSys}


        ${params.dataScope}


order by t.manufacture_order_code desc, t.create_date desc

mapper.update:
update s_man_day_manufacture_plan_item
plan_quantity = #{planQuantity}, item_num = #{itemNum}, plan_date = #{planDate}, remark = #{remark}, updater_account = #{updaterAccount}, update_date = #{updateDate}, data_source_sys = #{dataSourceSys},
where day_manufacture_plan_item_sid = #{dayManufacturePlanItemSid}

update.foreach:
update s_man_day_manufacture_plan_item
day_manufacture_plan_sid = #{dayManufacturePlanSid}, manufacture_order_sid = #{manufactureOrderSid}, manufacture_order_code = #{manufactureOrderCode}, material_sid = #{materialSid}, manufacture_order_process_sid = #{manufactureOrderProcessSid}, unit_base = #{unitBase}, plan_quantity = #{planQuantity}, plan_defective_quantity = #{planDefectiveQuantity}, item_num = #{itemNum}, remark = #{remark}, updater_account = #{updaterAccount}, update_date = #{updateDate}, data_source_sys = #{dataSourceSys},
where day_manufacture_plan_item_sid = #{dayManufacturePlanItemSid}
