mapper.select:



        and t6.customer_sid =#{customerSid}


        and t1.handle_status =#{handleStatus}


        and (t2.material_code like
        concat('%',
        #{item}, '%')
    )


        and (
        t9.manufacture_order_code like concat('%',
        #{item}, '%')
    )


        and t.plant_sid =#{plantSid}


        and (
        t1.manufacture_complete_note_code like concat('%',
        #{item}, '%')
    )


        and (t.client_id like concat('%',
        #{item}, '%')
    )


        and t.manufacture_complete_note_sid =#{manufactureCompleteNoteSid}


        and t.material_sid =#{materialSid}


        and t.sku1_sid =#{sku1Sid}


        and t.sku2_sid =#{sku2Sid}


        and t.barcode_sid =#{barcodeSid}

    and
        t.material_name like concat('%', #{materialName}, '%')


        and (t.unit_base like concat('%',
        #{item}, '%')
    )


        and t.complete_quantity = #{completeQuantity}


        and t.item_num =#{itemNum}


        and (t.creator_account like
        concat('%',
        #{item}, '%')
    )


        and date_format(t.create_date,'%y%m%d') >= date_format(#{beginTime},'%y%m%d')


        and date_format(t.create_date,'%y%m%d') <= date_format(#{endTime},'%y%m%d')


        and (t.updater_account like
        concat('%',
        #{item}, '%')
    )


        and t.update_date = #{updateDate}


        and (t.data_source_sys like
        concat('%',
        #{item}, '%')
    )


        ${params.dataScope}


order by t.create_date desc

mapper.update:
update s_man_manufacture_complete_note_item
client_id = #{clientId}, manufacture_complete_note_sid = #{manufactureCompleteNoteSid}, manufacture_order_product_sid = #{manufactureOrderProductSid}, material_sid = #{materialSid}, sku1_sid = #{sku1Sid}, sku2_sid = #{sku2Sid}, barcode_sid = #{barcodeSid}, material_name = #{materialName}, unit_base = #{unitBase}, complete_quantity = #{completeQuantity}, item_num = #{itemNum}, remark = #{remark}, creator_account = #{creatorAccount}, create_date = #{createDate}, updater_account = #{updaterAccount}, update_date = #{updateDate}, data_source_sys = #{dataSourceSys},
where manufacture_complete_note_item_sid = #{manufactureCompleteNoteItemSid}

update.foreach:
update s_man_manufacture_complete_note_item
client_id = #{clientId}, manufacture_complete_note_sid = #{manufactureCompleteNoteSid}, manufacture_order_product_sid = #{manufactureOrderProductSid}, material_sid = #{materialSid}, sku1_sid = #{sku1Sid}, sku2_sid = #{sku2Sid}, barcode_sid = #{barcodeSid}, material_name = #{materialName}, unit_base = #{unitBase}, complete_quantity = #{completeQuantity}, item_num = #{itemNum}, remark = #{remark}, creator_account = #{creatorAccount}, create_date = #{createDate}, updater_account = #{updaterAccount}, update_date = #{updateDate}, data_source_sys = #{dataSourceSys},
where manufacture_complete_note_item_sid = #{manufactureCompleteNoteItemSid}
