mapper.select:



        and (t.client_id like concat('%',
        #{item}, '%')
    )


        and t.manufacture_complete_note_sid =#{manufactureCompleteNoteSid}


        and t.manufacture_complete_note_sid in
        (
        #{manufactureCompleteNoteSid})


        and t.manufacture_complete_note_code like concat('%',#{manufactureCompleteNoteCode}, '%')


        and t.document_date = #{documentDate}


        and t.plant_sid =#{plantSid}


        and t.plant_sid in
        (#{plantSid})


        and t.manufacture_order_sid =#{manufactureOrderSid}


        and t.manufacture_order_code like concat('%',#{manufactureOrderCode}, '%')


        and (t3.material_code like
        concat('%',#{item}, '%'))


        and (t.document_type like
        concat('%',
        #{item}, '%')
    )


        and (t.business_type like
        concat('%',
        #{item}, '%')
    )


        and t.company_sid =#{companySid}


        and t.storehouse_sid =#{storehouseSid}


        and t.storehouse_location_sid =#{storehouseLocationSid}


        and (t.handle_status like
        concat('%',
        #{item}, '%')
    )


        and t.handle_status in
        (#{handleStatus})


        and t.creator_account in
        (#{item})


        and (t.creator_account like
        concat('%',
        #{item}, '%')
    )


        and date_format(t.create_date,'%y%m%d') >= date_format(#{beginTime},'%y%m%d')


        and date_format(t.create_date,'%y%m%d') <= date_format(#{endTime},'%y%m%d')


        and (t.updater_account like
        concat('%',
        #{item}, '%')
    )


        and t.update_date = #{updateDate}


        and (t.confirmer_account
        like concat('%',
        #{item}, '%')
    )


        and t.confirm_date = #{confirmDate}


        and (t.data_source_sys like
        concat('%',
        #{item}, '%')
    )


        ${params.dataScope}


group by t.manufacture_complete_note_sid
order by t.manufacture_complete_note_code desc, t.create_date desc

