mapper.select:



        and t.client_id = #{clientId}


        and t.manufacture_outsource_settle_sid =#{manufactureOutsourceSettleSid}


        and t.manufacture_outsource_settle_sid in
        (
        #{manufactureOutsourceSettleSid})


        and t.manufacture_outsource_settle_code like concat('%',#{manufactureOutsourceSettleCode}, '%')


        and t.vendor_sid =#{vendorSid}


        and t.vendor_sid in
        (#{vendorSid})


        and t.document_date = #{documentDate}


        and date_format(t.document_date,'%y%m%d') >= date_format(#{documentDateBegin},'%y%m%d')


        and date_format(t.document_date,'%y%m%d') <= date_format(#{documentDateEnd},'%y%m%d')


        and t.plant_sid =#{plantSid}


        and (t7.approval_user_id = #{approvalUserId}
        or t7.approval_user_id like concat(#{approvalUserId}, ',%') or t7.approval_user_id like concat('%,', #{approvalUserId}, ',%') or t7.approval_user_id like concat('%,', #{approvalUserId})
        or t7.approval_user_id like concat('%, ', #{approvalUserId}, ',%') or t7.approval_user_id like concat('%, ', #{approvalUserId})
        )


        and t.plant_sid in
        (#{plantSid})


        and t.document_type = #{documentType}


        and t.document_type in
        (#{documentType})


        and t.business_type = #{businessType}


        and t.business_type in
        (#{businessType})


        and t.company_sid =#{companySid}


        and t.company_sid in
        (#{companySid})


        and t.purchase_contract_sid = #{purchaseContractSid}


        and t.purchase_contract_code like concat('%', #{purchaseContractCode} , '%')


        and t.handle_status = #{handleStatus}


        and t.handle_status in
        (#{handleStatus})


        and (
        t.creator_account like concat('%',#{item}, '%')
    )


        and t.creator_account in
        (#{creatorAccount})


        and t4.nick_name like concat('%',#{creatorAccountName}, '%')


        and date_format(t.create_date,'%y%m%d') >= date_format(#{beginTime},'%y%m%d')


        and date_format(t.create_date,'%y%m%d') <= date_format(#{endTime},'%y%m%d')


        and (
        t.updater_account like concat('%',#{item}, '%')
    )


        and t.update_date = #{updateDate}


        and (
        t.confirmer_account like concat('%',#{item}, '%')
    )


        and t.confirm_date = #{confirmDate}


        and t.currency = #{currency}


        and t.currency_unit = #{currencyUnit}


        and t.data_source_sys = #{dataSourceSys}


        ${params.dataScope}


order by t.manufacture_outsource_settle_code desc, t.create_date desc

